public void run()
{
    super();
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventDimParm inventDimParmVisible;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetUp)
            {
                inventDimFormSetUp = InventDimCtrl_Frm_Setup::newFromForm(element);
                inventdimparmvisible.inventsiteidflag       = true;
                inventdimparmvisible.InventLocationIdFlag   = true;
                inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmVisible);
                inventDimFormSetup.formSetControls(true);
            }
            break;
        case InventDimFormDesignUpdate::Active:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(MCRRoyaltyItemTable.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;
        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(MCRRoyaltyItemTable.ItemId));
            InventDim.clearNotSelectedDim(inventDimFormSetup.parmDimParmEnabled());
            inventDimFormSetup.formSetControls( true);
            break;

        default: throw error(strfmt("@SYS54195", funcname()));
    }
}

void updateDesignCode(InventDimFormDesignUpdate mode)
{
    InventDimParm inventDimParmVisible;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!codeInventDimFormSetup)
            {
                codeInventDimFormSetup = InventDimCtrl_Frm_Setup::newFromForm(element);
                inventdimparmvisible.inventsiteidflag       = true;
                inventdimparmvisible.InventLocationIdFlag   = true;
                codeInventDimFormSetup.parmDimParmVisibleGrid(inventDimParmVisible);
                codeInventDimFormSetup.formSetControls(true);
            }
        case InventDimFormDesignUpdate::Active:
            codeInventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(MCRRoyaltyItemCode.ItemId));
            codeInventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange:
            codeInventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(MCRRoyaltyItemCode.ItemId));
            inventDimCode.clearNotSelectedDim(codeInventDimFormSetup.parmDimParmEnabled());
            codeInventDimFormSetup.formSetControls(true);
            break;

        default: throw error(strfmt("@SYS54195", funcname()));
    }
}

public void write()
{
    boolean newRecord;

    newRecord = MCRRoyaltyTable.RecId == 0;
    super();

    // This check is included to ensure that enableByStatus is called if a new record is created.
    if (newRecord)
    {
        element.enableByStatus();
    }
}

public void delete()
{
    super();

    // This check is included to ensure that enableByStatus is called if the last record in
    // the grid is deleted.
    if (!MCRRoyaltyTable.RecId)
    {
        element.enableByStatus();
    }
}

public int active()
{
    int ret;

    ret = super();

    element.enableByStatus();

    return ret;
}

public void create(boolean _append = false)
{
    numberSeq   numberSeq;
    super(_append);
    ttsBegin;
    numberSeq = NumberSeq::newGetNum(NumberSeqReference::findReference(extendedTypeNum(MCRRoyaltyCode)));

    MCRRoyaltyTable.RoyaltyCode = numberSeq.num();
    MCRRoyaltyTable.initFromMCRRoyaltyContract(MCRRoyaltyContract);
    ttsCommit;
}

