public void executeQuery()
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    queryBuildDataSource = query.addDataSource(tableNum(MCRRoyaltyContract));
    queryBuildDataSource.orderMode(OrderMode::OrderBy);
    queryBuildDataSource.addSortField(fieldnum(MCRRoyaltyContract, RoyaltyContract), SortOrder::Descending);
    // Filter based on royalty code. This is the goto main table from royalty calc (TAMRebateCalc).

    if (parmRoyaltyContractCode)
    {
        queryBuildDataSource.addRange(fieldnum(MCRRoyaltyContract, RoyaltyContract)).value(parmRoyaltyContractCode);
    }

    this.query(query);
    super();
}

public int active()
{
    int ret;
    ret = super();
    element.initControlPeriodType();
    element.enableByStatus();
    return ret;
}

public void executeQuery()
{
    Query   query;
    QueryBuildDataSource queryBuildDataSource;

    query = this.query();
    queryBuildDataSource = query.dataSourceTable(tableNum(MCRRoyaltyItemCode));
    queryBuildDataSource.clearRanges();
    queryBuildDataSource.addRange(fieldnum(MCRRoyaltyItemCode, RoyaltyCode)).value(MCRRoyaltyTable.RoyaltyCode);
    queryBuildDataSource.addRange(fieldnum(MCRRoyaltyItemCode, RoyaltyContract)).value(MCRRoyaltyTable.RoyaltyContract);
    this.query(query);
    super();
}

