public void create(boolean _append = false)
{
    MCRRoyaltyTablePricing   localMCRRoyaltyTablePricing;
    super(_append);
    select firstonly reverse localMCRRoyaltyTablePricing
            where localMCRRoyaltyTablePricing.RoyaltyCode == MCRRoyaltyItemCode.RoyaltyCode;
    if (localMCRRoyaltyTablePricing)
    {
        MCRRoyaltyTablePricing.UnitID           = localMCRRoyaltyTablePricing.UnitID;
        MCRRoyaltyTablePricing.PriceUnit        = localMCRRoyaltyTablePricing.PriceUnit;
    }
    else
    {
        MCRRoyaltyTablePricing.UnitID           = MCRRoyaltyTable.UnitID;
        MCRRoyaltyTablePricing.PriceUnit        = 1;
    }
}

