void clicked()
{
    FormRun formRun;
    Args    args = new Args();

    args.record(MCRRoyaltyContract);
    args.caller(element);

    formRun = new MenuFunction(menuitemDisplayStr(MCRRoyaltyContractAddItems), MenuItemType::Display).create(args);
    if (formRun)
    {
        formRun.run();
        formRun.wait();
        MCRRoyaltyItemTable_ds.executeQuery();
    }
}

void clicked()
{
    MCRRoyaltyItemCode           localMCRRoyaltyItemCode;
    ttsBegin;
    while select forUpdate localMCRRoyaltyItemCode
            where localMCRRoyaltyItemCode.RoyaltyCode == MCRRoyaltyTable.RoyaltyCode
    {
        localMCRRoyaltyItemCode.doDelete();
    }

    super();
    ttsCommit;
}

