void  createLines()
{
    InventDimId inventDimIdLocal;

    while select tmpFrmVirtualQty
    {
        inventDimIdLocal = element.tmpFrmVirtualInventDimId(tmpFrmVirtualQty.InventDimId, tmpFrmVirtualQty.ItemId);
        if (inventDimIdLocal)
        {
            tmpFrmVirtualQty.InventDimId = inventDimIdLocal;
            tmpFrmVirtualQty.update();
        }
    }

    if (!this.createLinesFromTmpFrmVirtual(tmpFrmVirtualQty))
    {
        throw error (Error::wrongUseOfFunction(funcName()));
    }

    salesQuickQuoteConfigure.parmRecordsCreated(true);
}

/// <summary>
/// Create lines from <c>TmpFrmVirtual</c> record.
/// </summary>
/// <param name="_tmpFrmVirtualQty">
/// A <c>TmpFrmVirtual</c> record.
/// </param>
/// <returns>
/// true if lines creation was successful; otherwise, false.
/// </returns>
/// <remarks>
/// This method is an extension point to control the creation of the lines
/// from other entities than the standard ones.
/// </remarks>
protected boolean createLinesFromTmpFrmVirtual(TmpFrmVirtual _tmpFrmVirtualQty)
{
    if (salesTable)
    {
        this.createSalesLinesFromTmpFrmVirtual(_tmpFrmVirtualQty);
        return true;
    }
    else if (salesQuotationTable)
    {
        this.createSalesQuotationLinesFromTmpFrmVirtual(_tmpFrmVirtualQty);
        return true;
    }
    return false;
}

private void createSalesLinesFromTmpFrmVirtual(TmpFrmVirtual _tmpFrmVirtualQty)
{
    SalesLine salesLineLocal;
    SalesLine salesLineContinuity;
    MCRContinuityCustHeader mcrContinuityCustHeader;

    if (salesTable.checkUpdate())
    {
        ttsbegin;

        salesLineLocal.createFromTmpFrmVirtual(salesTable, _tmpFrmVirtualQty);
        // Launch the continuity order form for each continuity item on the order
        while select RecId, PackedExtensions, InventTransId, SalesId, CustAccount from salesLineContinuity
                    where salesLineContinuity.SalesId == salesTable.SalesId
                exists join mcrContinuityCustHeader
                    where mcrContinuityCustHeader.InventTransId == salesLineContinuity.InventTransId
        {
            MCRContinuityCustHeader::launchContinuityForms(salesLineContinuity);
        }

        ttscommit;
    }
}

private void createSalesQuotationLinesFromTmpFrmVirtual(TmpFrmVirtual _tmpFrmVirtualQty)
{
    SalesQuotationLine  salesQuotationLineLocal;

    if (salesQuotationTable.checkUpdate())
    {
        salesQuotationLineLocal.mcrCreateFromTmpFrmVirtual(salesQuotationTable, _tmpFrmVirtualQty);
    }
}

void  doApply()
{
    // If the selected record is not in tmpFrmVirtualQty
    //  then this will be blank, so select to see if anything exists.
    select firstonly tmpFrmVirtualQty;

    if (tmpFrmVirtualQty)
    {
        Common currentRecord = element.args().record();
        FormDataSource callerDataSource = FormDataUtil::getFormDataSource(currentRecord);
        element.doApplyToTable();
        if (currentRecord)
        {
            callerDataSource.research(true);
        }
        else
        {
            callerDataSource.reread();
        }
    }

    super();
}

public void doApplyToTable()
{
    MCRInventTable      mcrInventTable;

    if (!mcrSalesQuickQuoteSumUnion)
    {
        throw error("@SYS82774");
    }

    mcrInventTable = MCRInventTable::findByItemId(mcrSalesQuickQuoteSumUnion.ItemId);

    if (!mcrInventTable
        || (!mcrInventTable.SellEndDate
        || (mcrInventTable.SellEndDate >= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))))
    {
        // sell start date validation
        if (!mcrInventTable
            || mcrInventTable.SellStartDate <= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))
        {
            element.createLines();
        }
        else
        {
            throw error(strFmt("@MCR34892", mcrSalesQuickQuoteSumUnion.ItemId, mcrInventTable.SellStartDate));
        }
    }
    else
    {
        throw error(strFmt("@MCR34893", mcrSalesQuickQuoteSumUnion.ItemId));
    }
}

/// <summary>
///     Prepares the product search.
/// </summary>
void prepareSearch()
{
    if (mcrSearchString.text())
    {
        MCRInventSearch inventSearch = this.inventSearch();
        productSearchResults = inventSearch.executeSearch(mcrSearchString.text());

        element.populateSearchTable();
    }
    else // Search string is blank;  clear results.
    {
        results.text('');
    }
}

/// <summary>
/// Gets an instance of <c>MCRInventSearch</c>.
/// </summary>
/// <returns>
/// <c>MCRInventSearch</c> instance.
/// </returns>
/// <remarks>
/// This method is an extension point to allow the modification of the search object
/// before the search is actually executed.
/// </remarks>
protected MCRInventSearch inventSearch()
{
    MCRInventSearch inventSearch = new MCRInventSearch();

    if (element.args().dataset())
    {
        inventSearch.parmSearchCaller(element.args().record());
    }

    return inventSearch;
}

private Common getCallerParentRecord()
{
    Object          callerDataSource;
    Common          callerRecord;
    Common          callerRecordOfCallerRecord;
    FormDataSource  formDataSource;

    callerRecord = element.args().record();
    callerDataSource = FormDataUtil::getFormDataSource(callerRecord);
    formDataSource = FormDataUtil::getFormDataSource(callerRecord);
    callerRecordOfCallerRecord = formDataSource.formRun().dataSource(1).cursor();

    return callerRecordOfCallerRecord;
}

/// <summary>
///     Reads the usage data to set the show positive quantity checkbox.
/// </summary>
/// <returns>
/// A <c>Boolean</c> of the value of the show positive quantity checkbox.
/// </returns>
boolean getPositiveQtyCheckBox()
{
    container c = xSysLastValue::getValue(curext(),
                                          curUserId(),
                                          UtilElementType::Enum,
                                          MCRItemAVAIL,
                                          MCRShowPOSITIVEQTY);

    // Version is stored in the first slot of the container.
    return conPeek(c, 2);
}

void init()
{
    super();

    inventAvailabilityFormCache = InventAvailabilityFormCache::construct();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    // Checks for parameters passed in, and updates the form appropriately.
    // Currently, the item info screen calls with these parameters
    if (element.args().parmObject())
    {
        salesQuickQuoteConfigure = element.args().parmObject();
        if (salesQuickQuoteConfigure.parmDisableForm())
        {
            element.mcrDisableForm();
        }

        mcrSearchString.text(salesQuickQuoteConfigure.parmSearchText());
        productSearchResults = salesQuickQuoteConfigure.parmSearchResults();
    }

    // clear out parm when finished reading stringMap
    element.args().parmObject(null);

    if (!this.setHeaderRecord())
    {
        throw error(strFmt("@MCR10663", element.name()));
    }

    element.inventDimSetupObject().initQuery(InventItemDimTmpFilter_ds);
    element.inventDimSetupObject().buildQuery(false);

    this.setupFallbackProductTranslation();
}

private void setupFallbackProductTranslation()
{
    List fallbackStringControls = new List(Types::Integer);
    List ecoResProductTranslationStringControls = new List(Types::Integer);

    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(MCRSalesQuickQuote, ecoResProductTranslation_Name)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(MCRSalesQuickQuote, EcoResProductTranslationFallbackView_Name)));

    EcoResProductTranslationFallbackViewModel::setupFallbackTranslationForm(element,
                                                                            element.EcoResProductTranslationFallbackView_ds,
                                                                            element.InventTable_ds,
                                                                            fallbackStringControls,
                                                                            ecoResProductTranslationStringControls);
}

/// <summary>
/// Sets the header record from the line record.
/// </summary>
/// <returns>
/// true if the header can be set; otherwise, false.
/// </returns>
/// <remarks>
/// This method is an extension point to control the setting of the header record.
/// </remarks>
protected boolean setHeaderRecord()
{
    TableId callerTableID = element.args().dataset();

    switch (callerTableID)
    {
        case tableNum(SalesLine):
            salesTable = element.getCallerParentRecord();
            return true;

        case tableNum(SalesQuotationLine):
            salesQuotationTable = element.getCallerParentRecord();
            return true;

        default:
            return false;
    }
}

