/// <summary>
///    Determines the inventory dimension ID to be used with the <c>tmpFrmVirtual</c> record.
/// </summary>
/// <param name="_inventDimIdLine">
///    The <c>InventDimId</c> that is used to determine the inventory dimension.
/// </param>
/// <param name="_itemId">
///    The <c>ItemId</c> that is used to determine the inventory dimension.
/// </param>
/// <returns>
///    A inventory dimension ID.
/// </returns>
public InventDimId tmpFrmVirtualInventDimId(InventDimId _inventDimIdLine, ItemId _itemId = '')
{
    InventDim               inventDimLocal;

    if (!_itemId || inventorydimensions_InventLocationId.visible()) // If warehouse is visible on the form
    {
        inventDimLocal = InventDim::find(_inventDimIdLine);
    }
    else
    {
        InventDim invDim = InventDim::find(_inventDimIdLine);

        if (!this.setInventLocationId(invDim, _inventDimIdLine, _itemId))
        {
            throw error (Error::wrongUseOfFunction(funcName()));
        }

        inventDimLocal   = InventDim::findOrCreate(invDim);
    }

    if (!inventDimLocal.InventSiteId)
    {
        if (salesTable && salesTable.InventSiteId)
        {
            inventDimLocal.InventSiteId     = salesTable.InventSiteId;
            inventDimLocal.InventLocationId = salesTable.InventLocationId;
        }
        else if (salesQuotationTable && salesQuotationTable.InventSiteId)
        {
            inventDimLocal.InventSiteId     = salesQuotationTable.InventSiteId;
            inventDimLocal.InventLocationId = salesQuotationTable.InventLocationId;
        }

        inventDimLocal   = InventDim::findOrCreate(inventDimLocal);
    }

    return inventDimLocal.InventDimId;
}

/// <summary>
/// Sets the invent location id.
/// </summary>
/// <param name="_invDim">
/// An <c>InventDim</c> record.
/// </param>
/// <param name="_inventDimIdLine">
/// The <c>InventDimId</c> that is used to determine the inventory dimension.
/// </param>
/// <param name="_itemId">
/// The <c>ItemId</c> that is used to determine the inventory dimension.
/// </param>
/// <returns>
/// true if the InventLocationId was set successfully; otherwise, false.
/// </returns>
/// <remarks>
/// This method is an extension point to control the setting of the InventLocationId field.
/// </remarks>
protected boolean setInventLocationId(InventDim _invDim, InventDimId _inventDimIdLine, ItemId _itemId)
{
    if (salesTable)
    {
        //If site on header and on line are equal and warehouse is defined on header, use location from header
        if (salesTable.InventSiteId == _invDim.InventSiteId && salesTable.InventLocationId)
        {
            _invDim.InventLocationId = salesTable.InventLocationId;
        }
        else // Otherwise set default warehouse for the site from line
        {
            _invDim.InventLocationId = element.defaultWarehouseFromSite(_itemId,_inventDimIdLine);
        }
        return true;
    }
    else if (salesQuotationTable)
    {
        if (salesQuotationTable.InventSiteId == _invDim.InventSiteId && salesQuotationTable.InventLocationId)
        {
            _invDim.InventLocationId = salesQuotationTable.InventLocationId;
        }
        else // Otherwise set default warehouse for the site from line
        {
            _invDim.InventLocationId = element.defaultWarehouseFromSite(_itemId, _inventDimIdLine);
        }
        return true;
    }
    return false;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init,
             InventDimFormDesignUpdate::LinkActive:
            element.inventDimSetupObject().initFromCaller();

        case InventDimFormDesignUpdate::Active:
            element.inventDimSetupObject().formActiveSetup();
            element.inventDimSetupObject().formSetControls(true, true);
            break;

        default:
            throw error(strFmt("@SYS54195", funcName()));
    }
}

private InventLocationId defaultWarehouseFromSite(ItemId _itemId, InventDimId _inventDimId)
{
    return InventTable::find(_itemId).salesInventLocationId('',_inventDimId, '');
}

public void executeQuery()
{
    inventAvailabilityFormCache.refreshCache();
    element.prepareSearch();

    super();
}

public edit SalesQty  salesQty(boolean set,
                                InventItemDimTmpFilter   _inventItemDimTmpFilter,
                                SalesQty    _qty)
{
    InventDim   inventDimTmp = InventDim::findOrCreate(formJoinedRecord(_inventItemDimTmpFilter, inventDim_ds));

    select forupdate tmpFrmVirtualQty
                where  tmpFrmVirtualQty.ItemId == _inventItemDimTmpFilter.ItemId
                    && tmpFrmVirtualQty.InventDimId == inventDimTmp.InventDimId;

    if (set)
    {
        if (_qty)
        {
            if (PdsGlobal::pdsIsCWItem(inventItemDimTmpFilter.ItemId))
            {
                tmpFrmVirtualQty.PdsCWQty = _qty;
            }
            else
            {
                tmpFrmVirtualQty.Qty = _qty;
            }

            tmpFrmVirtualQty.ItemId = _inventItemDimTmpFilter.ItemId;
            tmpFrmVirtualQty.InventDimId = inventDimTmp.InventDimId;
            tmpFrmVirtualQty.write();
        }
        else if (tmpFrmVirtualQty)
        {
            tmpFrmVirtualQty.delete();
        }
    }

    if (PdsGlobal::pdsIsCWItem(inventItemDimTmpFilter.ItemId))
    {
        return tmpFrmVirtualQty.PdsCWQty;
    }
    else
    {
        return tmpFrmVirtualQty.Qty;
    }
}

/// <summary>
/// Gets the the quantity available for physical reservation.
/// </summary>
/// <param name="_inventItemDimTmpFilter">
/// The <c>InventItemDimTmpFilter</c> record for which to find the availability.
/// </param>
/// <returns>
/// The quantity in the inventory unit.
/// </returns>
display public InventQtyAvailPhysical availPhysical(InventItemDimTmpFilter _inventItemDimTmpFilter)
{
    InventQtyAvailPhysical availPhysical;
    InventDim inventDimChild = formJoinedRecord(_inventItemDimTmpFilter, inventDim_ds);

    if (inventDimChild)
    {
        availPhysical = inventAvailabilityFormCache.availabilityByItemId(_inventItemDimTmpFilter.ItemId, inventDimChild).availPhysical();
    }

    return availPhysical;
}

