/// <summary>
/// Initialize the data with relation on product dimensions.
/// </summary>
public void init()
{
    super();

    QueryBuildDataSource qbds = this.queryBuildDataSource();
    var productDimensions = InventProductDimension::getEnumeratorForEnabledDimensions();

    while (productDimensions.moveNext())
    {
        InventProductDimension productDimension = productDimensions.current();
        FieldId inventItemDimTmpFilterFieldId = productDimension.fieldIdInTable(tableStr(InventItemDimTmpFilter));

        if (inventItemDimTmpFilterFieldId)
        {
            qbds.addLink(inventItemDimTmpFilterFieldId, productDimension.parmInventDimFieldId(), InventItemDimTmpFilter_ds.name());
        }
    }
}

