boolean modified()
{
    boolean ret = super();

    InventDim inventDimTmp = InventDim::findOrCreate(formJoinedRecord(inventItemDimTmpFilter, inventDim_ds));
    SalesQty tmpSalesQty = InventTable::calculateOrderQuantity(InventItemSalesSetup::find(inventItemDimTmpFilter.ItemId, inventDimTmp.InventDimId),
                                                    inventDimTmp.InventDimId,
                                                    this.realValue(),
                                                    InventTable::find(inventItemDimTmpFilter.ItemId).salesUnitId());

    if (tmpSalesQty != this.realValue())
    {
        inventItemDimTmpFilter_ds.salesQty(true, inventItemDimTmpFilter, tmpSalesQty);
    }

    return ret;
}

