Method.Source:
    [Form]
    public class MCRSalesStatsCustTable extends FormRun
    {
        MCRSalesStatsCustTable      salesStatsCustTable;
        CustTable                   custTable;
        UnitOfMeasureSymbol         unitIdInvent;
        UnitOfMeasureSymbol         unitIdSecondary;
        [FormObservable]
        boolean                     inventUnitDisplayed;
        FormRun                     callerForm;
}

public boolean canClose()
{
    boolean ret;
    Object  salesStatsCustTable_dataSource;

    ret = super();

    if (FormDataUtil::isFormDataSource(salesStatsCustTable))
    {
        salesStatsCustTable_dataSource = FormDataUtil::getFormDataSource(salesStatsCustTable);
        salesStatsCustTable_dataSource.reRead();
        salesStatsCustTable_dataSource.reFresh();
    }

    element.updateCustomerService();

    return ret;
}

/// <summary>
///     Sets form to display in inventory units and will only
///     show statistics for a given customer if customer is passed.
/// </summary>
public void init()
{
    args                    args;
    common                  record;
    MCRSalesStatsCustTable  MCRSalesStatsCustTable;
    MCRSalesStatsCust       localMCRSalesStatsCust;
    Object                  caller;

    inventUnitDisplayed = true;

    args = element.args();

    caller = args.caller();
     // check if caller is a FormRun
    if (SysDictClass::isEqualOrSuperclass(classIdGet(caller), classNum(FormRun)))
    {
        callerForm = args.caller();
    }

    if (args && args.record())
    {
        record = args.record();

        switch (record.TableId)
        {
            case (tableNum(custTable)):
                custTable = record;
                MCRSalesStatsCustTable = MCRSalesStatsCustTable::find(custTable.AccountNum);
                if (!MCRSalesStatsCustTable)
                {
                    localMCRSalesStatsCust = new MCRSalesStatsCust();
                    localMCRSalesStatsCust.calcStatsForCust(custTable.AccountNum);
                }
                break;
            case (tableNum(MCRSalesStatsCustTable)):
                salesStatsCustTable = record;
                custTable = CustTable::find(salesStatsCustTable.CustAccount);
                break;
            case (tableNum(smmBusRelTable)):
                custTable = CustTable::findByPartyRecId(smmBusRelTable::findRec(record.RecId).Party);
                if (!custTable)
                {
                    throw error("@SYS22539");
                }
                MCRSalesStatsCustTable = MCRSalesStatsCustTable::find(custTable.AccountNum);
                if (!MCRSalesStatsCustTable)
                {
                    localMCRSalesStatsCust = new MCRSalesStatsCust();
                    localMCRSalesStatsCust.calcStatsForCust(custTable.AccountNum);
                }
                break;
        }
    }

    super();

    displayInventUnit1.visible(inventUnitDisplayed);
    displaySalesUnit1.visible(!inventUnitDisplayed);
}

void updateCustomerService()
{
    // Update customer service form with updated data.
    if (element.args().caller()
        && element.args().caller().name() == formStr(MCRCustomerService)
        && formHasMethod(element.args().caller(), identifierStr(MCRRefReshSalesStats)))
    {
        element.args().caller().mcrRefReshSalesStats();
    }
}

/// <summary>
///     Shows statisics for the passed customer, otherwise will show for all cust accounts.
/// </summary>
public void init()
{
    QueryBuildDataSource    qbdsSalesStatsCust;

    super();

    if (custTable)
    {
        qbdsSalesStatsCust = this.query().dataSourceTable(tableNum(MCRSalesStatsCustTable));

        qbdsSalesStatsCust.addRange(fieldNum(MCRSalesStatsCustTable, CustAccount)).value(custTable.accountNum);
    }

    this.cacheAddMethod(tableMethodStr(MCRSalesStatsCustTable,displayCustName));
}

public display qty displayOrderQtyMode(MCRSalesStatsCustItem _mcrSalesStatsCustItem)
{
    if (inventUnitDisplayed)
    {
        return _mcrSalesStatsCustItem.OrderQtyMode;
    }
    else
    {
        return EcoResProductUnitConverter::convertGivenUnitSymbolsForReleasedProduct(_mcrSalesStatsCustItem.ItemId,
                                                                                     _mcrSalesStatsCustItem.getVariantDimensionId(),
                                                                                     _mcrSalesStatsCustItem.OrderQtyMode,
                                                                                     MCRSalesStatsCustItem_ds.displayInventUnit(_mcrSalesStatsCustItem),
                                                                                     MCRSalesStatsCustItem_ds.displaySalesUnit(_mcrSalesStatsCustItem),
                                                                                     NoYes::Yes);
    }
}

