public display UnitOfMeasureSymbol displayInventUnit(MCRSalesStatsCustItem _mcrSalesStatsCustItem)
{
    return InventTableModule::find(_mcrSalesStatsCustItem.ItemId, ModuleInventPurchSales::Invent).UnitId;
}

public display unitOfMeasureSymbol displaySalesUnit(MCRSalesStatsCustItem _mcrSalesStatsCustItem)
{
    return InventTableModule::find(_mcrSalesStatsCustItem.ItemId, ModuleInventPurchSales::Sales).UnitId;
}

/// <summary>
///     Re-queries the item statistics if statistics are regenerated.
/// </summary>
void clicked()
{
    int h;

    super();

    MCRSalesStatsCust_ds.reread();
    MCRSalesStatsCust_ds.refresh();
    MCRSalesStatsCustItem_ds.executeQuery();

    if (callerForm && callerForm.name() == formStr(MCRCustomerService))
    {
        for (h = 1;  h <= callerForm.dataSourceCount();  h++)
        {
            if (callerForm.dataSource(h).cursor().TableId == tableNum(CustTable))
            {
                callerForm.dataSource(h).active(); // call active so the super will call to refresh the statistics form part
                return;
            }
        }
    }
}

/// <summary>
///     Hides sales unit fields when displaying in inventory units.
/// </summary>
void clicked()
{
    super();

    inventUnitDisplayed = true;

    displayInventUnit1.visible(inventUnitDisplayed);
    displaySalesUnit1.visible(!inventUnitDisplayed);

    MCRSalesStatsCustItem_ds.refresh();
}

/// <summary>
///     Hides inventory unit fields when displaying in sales units.
/// </summary>
void clicked()
{
    super();

    inventUnitDisplayed = false;

    displayInventUnit1.visible(inventUnitDisplayed);
    displaySalesUnit1.visible(!inventUnitDisplayed);

    MCRSalesStatsCustItem_ds.refresh();
}
