/// <summary>
///     Updates sales history to display existing sales order attriubtes.
/// </summary>
void mcrSetLineControls()
{
    boolean             isEmailGiftCert = false;
    boolean             isMailGiftCert = false;
    boolean             isContinuityLine = false;
    boolean             canHavePriceHistory;
    MCRPriceHistoryLine mcrPriceHistoryLine;

    if (salesline.RecId && mcrSalesLine.GiftCardNumber)
    {
        isEmailGiftCert = (mcrSalesLine.GiftCardType == MCRGiftCardType::Email);
        isMailGiftCert = (mcrSalesLine.GiftCardType == MCRGiftCardType::Physical);
    }

    isContinuityLine = (salesLine.mcrSalesLine().ContinuityScheduleId != '');

    // don't show if item is not a gift certificate
    GiftCardVirtual.visible(isEmailGiftCert || isMailGiftCert);

    LineSalesTaxAddress.visible( ! (isEmailGiftCert || isMailGiftCert));
    MCRDeliveryAddressLeft.visible(!isEmailGiftCert);
    MCRDeliveryAddressMiddle.visible(!isEmailGiftCert);
    MCRDeliveryAddress_MCRDeliveryName.visible(!(isEmailGiftCert || isMailGiftCert));
    MCRSalesLine_ds.object(FieldNum(MCRSalesLine, GiftCardRecipientEmail)).visible(isEmailGiftCert);
    MCRSalesLine_ds.object(FieldNum(MCRSalesLine, GiftCardBuyerEmail)).visible(isEmailGiftCert);

    MCRContinuitySchedule.enabled(isContinuityLine);

    // Enable/disable price history
    mcrPriceHistoryLine = MCRPriceHistoryLine::construct(salesLine);
    canHavePriceHistory = mcrPriceHistoryLine.allowView();
    MCRPriceHistory.enabled(canHavePriceHistory);
}

/// <summary>
///     Updates the form and sets the controls.
/// </summary>
/// <param name="mode">
/// The form mode to change the form to.
/// </param>
void updateDesign(InventDimFormDesignUpdate mode)
{
    InventMovement movement = InventMovement::construct(salesLine);

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_frm_mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newInventTable(movement.inventTable()), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newInventTable(movement.inventTable()), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default:
            throw error(strfmt("@SYS54195", funcname()));
    }
}

