/// <summary>
///     Sets the background color.
/// </summary>
/// <param name="_record">
/// The record in which to set the background color.
/// </param>
/// <param name="_options">
/// The display options to use.
/// </param>
public void displayOption(Common _record,
                          FormRowDisplayOption _options)
{
    MCRHoldOrderDisplayColor mcrHoldOrderDisplayColor;

    SalesTable salesTableLocal = _record;

    mcrHoldOrderDisplayColor = SalesParameters::find().MCRHoldOrderDisplayColor;

    if (mcrHoldOrderDisplayColor
        && MCRHoldCodeTrans::findActiveOrderHolds(salesTableLocal.SalesId))
    {
        _options.backColor(mcrHoldOrderDisplayColor);
    }
    else
    {
        _options.clearBackColor();
    }

    super(_record, _options);
}

/// <summary>
/// Override of the executeQuery method.
/// Adding grid sorting by Sales order id in descending order.
/// </summary>
public void executeQuery()
{
    QueryBuildDataSource queryBuildDataSource;

    queryBuildDataSource = this.query().dataSourceName(SalesTable_ds.name());

    queryBuildDataSource.sortClear();
    queryBuildDataSource.addsortField(fieldNum(SalesTable, SalesId), SortOrder::Descending);

    super();
}

/// <summary>
///     Filters out ship to parent lines.
/// </summary>
public void init()
{
    QueryBuildDataSource queryBuildDataSource;

    super();

    queryBuildDataSource = this.query().dataSourceTable(tableNum(SalesLine));
    queryBuildDataSource.addRange(fieldNum(Salesline, LineDeliveryType)).value(SysQuery::valueNot(LineDeliveryType::OrderLineWithMultipleDeliveries));

    this.cacheAddMethod(tableMethodStr(SalesLine,MCRDeliverpickedInTotalSalesUnit));
}

public void displayOption(Common _record,
                          FormRowDisplayOption _options)
{
    SalesLine           salesLineLocal          = _record;
    MCROrderParameters  mcrOrderParameters      = MCROrderParameters::find();
    int                 priceOverrideLineColor  = mcrOrderParameters.MCRPriceOverrideLineColor;

    // if the price was overridden on this line, set the Price Override Line Color from the sales parameter table.
    // Child Ship To Lines always have there price overriden by the system - don't display as overridden
    // **note:  child ship to line price cannot be changed & is driven by the parent line.
    if (priceOverrideLineColor
        && salesLineLocal.mcrSalesLinePriceOverride().PriceOverride == NoYes::Yes
        && salesLineLocal.LineDeliveryType != LineDeliveryType::DeliveryLine)
    {
        _options.backColor(priceOverrideLineColor);
    }
    else
    {
        _options.clearBackColor();
    }

    super(_record, _options);
}

public int active()
{
    int ret;

    ret = super();

    element.mcrSetLineControls();

    return ret;
}

public void executeQuery()
{
    QueryBuildDataSource queryBuildDataSource;
    str                  relatedOrdersQuery;

    queryBuildDataSource = this.query().dataSourceName(SalesTableRelatedOrders_ds.name());

    queryBuildDataSource.clearRanges();
    queryBuildDataSource.clearDynalinks();
    queryBuildDataSource.addsortField(fieldNum(SalesTable, SalesId), SortOrder::Descending);
    queryBuildDataSource.addRange(FieldNum(SalesTable, CustAccount)).value(SalesTable.CustAccount);

    if (SalesTable.SalesId)
    {
        relatedOrdersQuery = this.relatedOrdersQueryStr();
        if (relatedOrdersQuery)
        {
            queryBuildDataSource.addRange(FieldNum(SalesTable, SalesId)).value(relatedOrdersQuery);
        }
        else
        {
            queryBuildDataSource.addRange(FieldNum(SalesTable, SalesId)).value(sysQuery::valueEmptyString());
        }
    }
    else
    {
        queryBuildDataSource.addRange(FieldNum(SalesTable, SalesId)).value(sysQuery::valueEmptyString());
    }

    super();
}

        /// <summary>
        ///     Creates a related orders query string.
        /// </summary>
        /// <returns>
        /// A related orders query string.
        /// </returns>
        str relatedOrdersQueryStr()
        {
            str             returnQueryStr = '';
            SalesTable      salesTableLocal;
            SalesTableLinks salesTableLocalLinks;
            SalesTable      returnSalesTable;
            MCRReturnSalesTable mCRReturnSalesTable;

            salesTableLocal.SalesId = SalesTable.SalesId;

            if (SalesTable.SalesType == salestype::Sales && SalesTable.ReturnItemNum)
            {
                returnSalesTable = SalesTable::findReturnItemNum(SalesTable.ReturnItemNum);
                returnQueryStr = returnQueryStr + returnSalesTable.SalesId + ', ' +
                                                    returnSalesTable.mcrReturnSalesTable().OriginalSalesId;
            }
            else if (SalesTable.salesType == SalesType::ReturnItem)
            {
                returnQueryStr = returnQueryStr + SalesTable.ReturnReplacementId + ', ' +
                                                    SalesTable.mcrReturnSalesTable().OriginalSalesId;
            }
            else // means it is an original order
            {
                // grab all the children of the original order.
                while select SubSalesId from salesTableLocalLinks where salesTableLocalLinks.MainSalesId == SalesTable.SalesId
                {
                    returnQueryStr = returnQueryStr + salesTableLocalLinks.SubSalesId + ', ';
                }

                // grab all the return and replacement orders of the original order.
				while select returnSalesTable
                    join mCRReturnSalesTable
                    where mCRReturnSalesTable.SalesTable == returnSalesTable.RecId
                    && mCRReturnSalesTable.OriginalSalesId == SalesTable.SalesId
                {
                    returnQueryStr = returnQueryStr + returnSalesTable.SalesId +  ', ' + returnSalesTable.ReturnReplacementId + ', ';
                }
            }

            return returnQueryStr;
        }

