void clicked()
{
    Args args = new Args();
    FormRun   menuFunction;
    SalesTable      currentRecord;

    // save currently selected record
    currentRecord = SalesTable.data();

    args.caller(element);
    args.record(SalesTable);
    menuFunction = new Menufunction('MCRHoldCodeTransSOManage', menuitemtype::Display).create(args);
    menuFunction.run();
    menuFunction.wait();
    SalesTable_ds.executeQuery();
    // re-select the previously selected record
    SalesTable_ds.positionToRecord(currentRecord);
}

