public void closeOk()
{
    Set                 selectedRecIds = new Set(typeName2Type(extendedTypeStr(recId)));
    MarkupAllocation    markupAllocation = element.args().caller();

    selectedRecIds = markupAllocationSelectionManager.getSelectedRecIdsSet();

    if (useSpecificLines.value() == NoYes::Yes && !selectedRecIds.elements() || !markupAllocation.checkCloseDialog())
    {
        checkFailed("@SYS309189");
    }
    else
    {
        super();
        if (selectedRecIds != null)
        {
            markupAllocation.parmAllocationRecIds(selectedRecIds);
        }
        markupAllocation.parmAllocateAfter(any2Enum(allocateAfter.selection()));
        markupAllocation.parmAllocateOn(allocateOn.selection());
        markupAllocation.parmAllocateAll(allocateAll.value());
        markupAllocation.parmAllocateOnlyReceived(allocateOnlyReceived.value());
        markupAllocation.parmAllocateInventoried(allocateInventoried.value());
    }
}

/// <summary>
/// Sets data source query ranges during initial form load based on the buffer passed in from caller.
/// </summary>
/// <exception cref="Exception::Error" >
/// Error is thrown if the form has no caller or active buffer.
/// </exception>
public void init()
{
    super();
    if (!element.args().caller() || !element.args().record())
    {
        throw error("@SYS22539");
    }

    markupAllocationSelectionManager = MarkupAllocationSelectionManager::construct();
    markupAllocationFormHelper = MarkupAllocationFormHelper::construct();

    if (!this.initBasedOnTableBuffer())
    {
        throw error(strFmt("@SYS25516",element.name()));
    }

    markupAllocationFormHelper.parmHasInitialGridLoadFinished(false);
    element.updateDesign(InventDimFormDesignUpdate::Init);
    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        allocateOnlyReceived.helpText("@GLS106967");
    }
    // </GEERU>

    markAllTracker = new FormObservableLink();
}

/// <summary>
/// Initializes the form based on the table buffer passed.
/// </summary>
/// <returns>true if form initialization was done for the table buffer; otherwise, false.</returns>
public boolean initBasedOnTableBuffer()
{
    MarkupTrans markupTransLoc;

    switch (element.args().record().TableId)
    {
        case(tableNum(PurchTable)):
            purchLine_Q.dataSourceName(identifierStr(PurchLine)).addRange(fieldNum(PurchLine, PurchId)).
                value(element.args().record().getFieldValue(fieldId2name(tableNum(PurchTable), fieldNum(PurchTable, PurchId))));

            if (!MarkupTrans::findValue(
                    element.args().record().TableId,
                    element.args().record().getFieldValue(fieldId2name(tableNum(PurchTable), fieldNum(PurchTable, RecId)))))
            {
                useSpecificLines.enabled(false);
            }
            else
            {
                if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
                {
                    markupTransLoc = MarkupTrans::findValue(
                                            element.args().record().TableId,
                                            element.args().record().getFieldValue(fieldid2name(tablenum(PurchTable), fieldnum(PurchTable, RecId))));

                    if (markupTransLoc.MarkupCategory == MarkupCategory::Percent)
                    {
                        allocateAfter.enumType(enumnum(MarkupAllocateAfter_IN));
                    }
                }
            }

            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]) && !LedgerParameters::find().Miscellaneouscharges_IN)
            {
                allocateAfter.enumType(enumnum(MarkupAllocateAfter_IN));
            }
            markupAllocationSelectionManager.setAllowEditToFalse(purchLine_DS);

            this.showWarningForImportOrder(element.args().record());
            return true;

        case(tableNum(SalesTable)):
            salesLine_Q.dataSourceName(identifierStr(SalesLine)).addRange(fieldNum(SalesLine, SalesId)).
                value(element.args().record().getFieldValue(fieldId2name(tableNum(SalesTable), fieldNum(SalesTable, SalesId))));
            allocateOnlyReceived.visible(false);
            if (!MarkupTrans::findValue(
                    element.args().record().TableId,
                    element.args().record().getFieldValue(fieldId2name(tableNum(SalesTable), fieldNum(SalesTable, RecId)))))
            {
                useSpecificLines.enabled(false);
            }
            else
            {
                if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
                {
                    markupTransLoc = MarkupTrans::findValue(
                                            element.args().record().TableId,
                                            element.args().record().getFieldValue(fieldid2name(tablenum(SalesTable), fieldnum(SalesTable, RecId))));

                    if (markupTransLoc.MarkupCategory == MarkupCategory::Percent)
                    {
                        allocateAfter.enumType(enumnum(MarkupAllocateAfter_IN));
                    }
                }
            }

            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]) && !LedgerParameters::find().Miscellaneouscharges_IN)
            {
                allocateAfter.enumType(enumnum(MarkupAllocateAfter_IN));
            }
            markupAllocationSelectionManager.setAllowEditToFalse(SalesLine_DS);
            return true;

        case(tableNum(VendInvoiceInfoSubTable)):
            vendInvoiceInfoLine_Q.dataSourceName(identifierStr(VendInvoiceInfoLine)).addRange(fieldNum(VendInvoiceInfoLine, OrigPurchId)).
                value(element.args().record().
                getFieldValue(fieldId2name(tableNum(VendInvoiceInfoSubTable), fieldNum(VendInvoiceInfoSubTable, OrigPurchId))));
            vendInvoiceInfoLine_Q.dataSourceName(identifierStr(VendInvoiceInfoLine)).addRange(fieldNum(VendInvoiceInfoLine, ParmId)).
                    value(element.args().record().
                    getFieldValue(fieldId2name(tableNum(VendInvoiceInfoSubTable), fieldNum(VendInvoiceInfoSubTable, ParmId))));
            allocateOnlyReceived.visible(false);
            if (!MarkupTrans::findValue(
                    element.args().record().TableId,
                    element.args().record().getFieldValue(fieldId2name(tableNum(VendInvoiceInfoSubTable), fieldNum(VendInvoiceInfoSubTable, RecId)))))
            {
                useSpecificLines.enabled(false);
            }
            markupAllocationSelectionManager.setAllowEditToFalse(vendInvoiceInfoLine_DS);
            return true;

        case(tableNum(VendInvoiceInfoTable)):
            vendInvoiceInfoLine_Q.dataSourceName(identifierStr(VendInvoiceInfoLine)).addRange(fieldNum(VendInvoiceInfoLine, TableRefId)).
                value(element.args().record().
                getFieldValue(fieldId2name(tableNum(VendInvoiceInfoTable), fieldNum(VendInvoiceInfoTable, TableRefId))));
            vendInvoiceInfoLine_Q.dataSourceName(identifierStr(VendInvoiceInfoLine)).addRange(fieldNum(VendInvoiceInfoLine, ParmId)).
                    value(element.args().record().
                    getFieldValue(fieldId2name(tableNum(VendInvoiceInfoTable), fieldNum(VendInvoiceInfoTable, ParmId))));
            allocateOnlyReceived.visible(false);
            if (!MarkupTrans::findValue(
                    element.args().record().TableId,
                    element.args().record().getFieldValue(fieldId2name(tableNum(VendInvoiceInfoTable), fieldNum(VendInvoiceInfoTable, RecId)))))
            {
                useSpecificLines.enabled(false);
            }

            markupAllocationSelectionManager.setAllowEditToFalse(vendInvoiceInfoLine_DS);
            return true;

        case tableNum(PurchParmTable):
            if (TaxIntegrationUtils::isChargeOnBOEEnabled())
            {
                this.initDesignForBOE();
                return true;
            }
            return false;

        case(tablenum(MarkupTrans)):
            if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
            {
                markupTransLoc = element.args().record();
                PurchTable  purchTable;
                select firstonly purchId from purchTable
                        where purchTable.TableId == markupTransLoc.TransTableId
                           && purchTable.RecId   == markupTransLoc.TransRecId;

                purchLine_Q.dataSourceName(identifierstr(PurchLine)).clearDynalinks();
                purchLine_Q.dataSourceName(identifierstr(PurchLine)).addRange(fieldnum(PurchLine, PurchId)).
                        value(queryValue(purchTable.PurchId));
                purchLine_Q.dataSourceName(identifierstr(PurchLine)).addRange(fieldnum(PurchLine, PurchStatus)).value(queryValue(PurchStatus::None));
                purchLine_Q.dataSourceName(identifierstr(PurchLine)).addRange(fieldnum(PurchLine, PurchStatus)).value(queryValue(PurchStatus::Backorder));
                purchLine_Q.dataSourceName(identifierstr(PurchLine)).addRange(fieldnum(PurchLine, PurchStatus)).value(queryValue(PurchStatus::Received));

                if (!MarkupTrans::findValue(
                         markupTransLoc.TransTableId,
                         markupTransLoc.TransRecId))
                {
                    useSpecificLines.enabled(false);
                }
                else
                {
                    if (markupTransLoc.MarkupCategory == MarkupCategory::Percent)
                    {
                        allocateAfter.enumType(enumnum(MarkupAllocateAfter_IN));
                    }
                }

                if (!LedgerParameters::find().Miscellaneouscharges_IN)
                {
                    allocateAfter.enumType(enumnum(MarkupAllocateAfter_IN));
                }
                return true;
            }
    }
    return false;

}

private void initDesignForBOE()
{
    allocateOnlyReceived.visible(false);
    allocateInventoried.visible(false);
    useSpecificLines.visible(false);

    allocateAfter.enumType(enumnum(MarkupAllocateAfter_IN));
}

/// <summary>
/// Initializes the grid control and the corresponding data source based on buffer passed in from <c>upDateDesign</c>.
/// </summary>
///<param name="_tableId">
/// A TableId value that specifies which grid control to initialize.
///</param>
public void initGrid(tableId _tableId)
{
    allocateOn.enabled(false);
    allocateInventoried.enabled(false);
    markupAllocationSelectionManager.init();
    switch (_tableId)
    {
        case(tableNum(PurchTable)):
                purchLineGrid.visible(true);
                purchLine_DS.executeQuery();
                break;

        case(tableNum(SalesTable)):
                salesLineGrid.visible(true);
                salesLine_DS.executeQuery();
                break;

        case(tableNum(VendInvoiceInfoSubTable)), (tableNum(VendInvoiceInfoTable)):
                vendInvoiceInfoLineGrid.visible(true);
                vendInvoiceInfoLine_ds.executeQuery();
                break;
        // <GIN>
        case(tablenum(MarkupTrans)):
            if (LedgerParameters::isMiscellaneouscharges_IN())
            {
                purchLineGrid.visible(true);
                purchLine_DS.executeQuery();
            }
            break;
        // </GIN>
    }
}

/// <summary>
/// Updates the form design based on buffer passed in from caller.
/// </summary>
///<param name="_mode">
/// An <c>InventDimFormDesignUpdate</c> value that specifies the state of the form.
///</param>
public void updateDesign(InventDimFormDesignUpdate _mode)
{
    tableId         tableId = element.args().record().TableId;
    InventMovement  movement;

    if (tableId == tableNum(SalesTable))
    {
        movement = InventMovement::construct(salesLine);
    }

    else
    {
        movement = InventMovement::construct(purchLine);
    }

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }

    if (useSpecificLines.value() == NoYes::Yes)
    {
        element.initGrid(tableId);
    }
    else
    {
        purchLineGrid.visible(false);
        salesLineGrid.visible(false);
        vendInvoiceInfoLineGrid.visible(false);
        allocateOn.enabled(true);
        allocateInventoried.enabled(true);
        markupAllocationSelectionManager.init();
        markupAllocationSelectionManager.clearAndPopulateFullSet();
    }
}

/// <summary>
/// Displays a progress bar if loading more than 2500 lines.
/// </summary>
///<remarks>
/// Will not execute if <c>PurchLineGridGroup</c> is not visible.
///</remarks>
public void executeQuery()
{
        if (markupAllocationFormHelper.updateDesign(useSpecificLines.value()))
    {
        purchLine_DS.query(markupAllocationSelectionManager.setQueryRanges(element.args().record().TableId, purchLine_Q, allocateOn.selection(),
                      allocateInventoried.value(), purchLine_DS.name()));
        super();
        markupAllocationSelectionManager = markupAllocationFormHelper.markRecords(purchLine_DS, markupAllocationSelectionManager);
    }
}

edit NoYes marked(boolean set, PurchLine _purchLine, NoYes _isMarked)
{
    if (set)
    {
        if (include.value() == 1)
        {
            markupAllocationSelectionManager.insertRemoveSelectedRecIdsSet(purchLine.RecId, AddRemove::Add);
        }
        else
        {
            markupAllocationSelectionManager.insertRemoveSelectedRecIdsSet(purchLine.RecId, AddRemove::Remove);
            selectAll.enabled(true);
        }
    }

    markAllTracker.observe();

    if (markupAllocationSelectionManager.isMarked(_purchLine.RecId))
    {
        _isMarked = NoYes::Yes;
    }
    else
    {
        _isMarked = NoYes::No;
    }
    return _isMarked;
}

public void init()
{
    QueryBuildRange rangeIsDeleted;

    super();

    rangeIsDeleted = this.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine,IsDeleted));
    rangeIsDeleted.value(SysQuery::value(NoYes::No));
    rangeIsDeleted.status(RangeStatus::Hidden);
}

