void closeOk()
{
    Set selectedRecIds = markupAllocationSelectionManager.getSelectedRecIdsSet();

    if (useSpecificLines.value() == NoYes::Yes && !selectedRecIds.elements() || !caller.checkCloseDialog())
    {
        checkFailed("@SYS309189");
    }
    else
    {
        super();
        if (selectedRecIds != null)
        {
            caller.parmAllocationRecIds(selectedRecIds);
        }
        caller.setParameters(any2Enum(allocateafter.selection()), allocateon.selection(), postingdate.dateValue(), storno.value(), allocateInventoried.value());
    }
}

/// <summary>
/// Sets data source query ranges during initial form load.
/// </summary>
/// <exception cref="Exception::Error" >
/// Error is thrown if the active form buffer is not a <c>vendInvoiceJour</c> type buffer.
/// </exception>
void  init()
{
    super();

    caller = element.args().caller();
    custInvoiceJour = caller.custInvoiceJour();

    if (!custInvoiceJour)
        throw error("@SYS4205");

    transTableId    = markupTrans_ds.query().dataSourceTable(tablenum(MarkupTrans)).addRange(fieldnum(MarkupTrans, TransTableId));
    transRecId      = markupTrans_ds.query().dataSourceTable(tablenum(MarkupTrans)).addRange(fieldnum(MarkupTrans, TransRecId));
    voucher         = markuptrans_ds.query().dataSourceTable(tablenum(MarkupTrans)).addRange(fieldnum(MarkupTrans, Voucher));
    custInvoiceTrans_Q.dataSourceName(identifierstr(CustInvoiceTrans)).addRange(fieldnum(CustInvoiceTrans, SalesId)).value(custInvoiceJour.SalesId);
    custInvoiceTrans_Q.dataSourceName(identifierstr(CustInvoiceTrans)).addRange(fieldnum(CustInvoiceTrans, InvoiceId)).value(custInvoiceJour.InvoiceId);

    callerTableId   = custInvoiceJour.TableId;
    callerRecId     = custInvoiceJour.RecId;

    moduleType      = MarkupModuleType::Cust;

    transTableId.value  (queryValue(callerTableId));
    transRecId.value    (queryValue(callerRecId));
    voucher.value(SysQuery::valueEmptyString()); // Not Invoiced.

    postingdate.dateValue(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    markupAllocationSelectionManager = MarkupAllocationSelectionManager::construct();
    markupAllocationFormHelper = MarkupAllocationFormHelper::construct();
    markupAllocationFormHelper.parmHasInitialGridLoadFinished(false);
    useSpecificLines.enabled(MarkupTrans::findValue(callerTableId, callerRecId).RecId);
    element.updateDesign(InventDimFormDesignUpdate::Init);

    markupAllocationSelectionManager.setAllowEditToFalse(CustInvoiceTrans_DS);

    allocateafter.visible(false);
    allocateon.visible(false);
    storno.value(CustParameters::find().CreditError);
}

public void updateDesign(InventDimFormDesignUpdate _mode)
{
    SalesLine       salesLine;
    InventMovement  movement = InventMovement::construct(salesLine);

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }

        case InventDimFormDesignUpdate::Active :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strfmt("@SYS54195",funcname()));
    }
}

void linkActive()
{
    caller = element.args().caller();
    custInvoiceJour = caller.custInvoiceJour();

    callerTableId   = custInvoiceJour.TableId;
    callerRecId     = custInvoiceJour.RecId;

    transTableId.value  (queryValue(callerTableId));
    transRecId.value    (queryValue(callerRecId));
    voucher.value(SysQuery::valueEmptyString());
    super();
}

void  initValue()
{
    markupTrans.TransTableId    = callerTableId;
    markupTrans.TransRecId      = callerRecId;

    super();

    markupTrans.ModuleCategory  = HeadingLine::Heading;
    markupTrans.ModuleType      = moduleType;
}

int active()
{
    int     ret;

    ret = super();

    if (!markupTrans.markupTable().allowEditCurrency())
    {
        markupTrans_ds.object(fieldnum(MarkupTrans, CurrencyCode)).allowEdit(false);
    }
    else
    {
        markupTrans_ds.object(fieldnum(MarkupTrans, CurrencyCode)).allowEdit(true);
    }
    return ret;
}

public void init()
{
    QueryBuildRange rangeIsDeleted;

    super();

    rangeIsDeleted = this.query().dataSourceTable(tablenum(MarkupTrans)).addRange(fieldnum(MarkupTrans,IsDeleted));
    rangeIsDeleted.value(SysQuery::value(NoYes::No));
    rangeIsDeleted.status(RangeStatus::Hidden);
}

boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = markupTrans.checkMarkupCodeAdjustment(moduleType);
    }

    return ret;
}

void modified()
{
    super();

    markupTrans.initFromCustInvoiceJour_RU(caller.custInvoiceJour());

    if (!markupTrans.markupTable().allowEditCurrency())
    {
        markupTrans_ds.object(fieldnum(MarkupTrans, CurrencyCode)).allowEdit(false);
    }
    else
    {
        markupTrans_ds.object(fieldnum(MarkupTrans, CurrencyCode)).allowEdit(true);
    }
}

edit NoYes marked(boolean set, CustInvoiceTrans _custInvoiceTrans, NoYes _isMarked)
{
    AddRemove      addOrRemove;
    boolean        transIsMarked;

    if (set)
    {
        addOrRemove = include.value() == #isIncludeChecked ? AddRemove::Add : AddRemove::Remove;
        markupAllocationSelectionManager.insertRemoveSelectedRecIdsSet(custInvoiceTrans.RecId, addOrRemove);
        if (addOrRemove == AddRemove::Remove)
        {
            selectAll.enabled(true);
        }
    }
    transIsMarked = markupAllocationSelectionManager.isMarked(_custInvoiceTrans.RecId);
    _isMarked = (transIsMarked? NoYes::Yes : NoYes::No);

    return _isMarked;
}

public void executeQuery()
{
    if (markupAllocationFormHelper.updateDesign(useSpecificLines.value()))
    {
        custInvoiceTrans_DS.query(markupAllocationSelectionManager.setQueryRanges(element.args().record().TableId, custInvoiceTrans_Q, allocateOn.selection(),
                  allocateInventoried.value(), custInvoiceTrans_DS.name()));
        super();
        markupAllocationSelectionManager = markupAllocationFormHelper.markRecords(custInvoiceTrans_DS, markupAllocationSelectionManager);
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (markupAllocationFormHelper.updateDesign(useSpecificLines.value()))
    {
        allocateOn.enabled(false);
        allocateInventoried.enabled(false);
        allocateSpecific.visible(true);
        markupAllocationSelectionManager.init();
        custInvoiceTrans_DS.executeQuery();
        selectionButtons.visible(true);
    }
    else
    {
        allocateOn.enabled(true);
        allocateInventoried.enabled(true);
        allocateSpecific.visible(false);
        selectionButtons.visible(false);
        markupAllocationSelectionManager.init();
        markupAllocationSelectionManager.clearAndPopulateFullSet();
        markupAllocationFormHelper.parmHasInitialGridLoadFinished(false);
    }

    return ret;
}
