OffPositionStr_RU positionEdit(boolean _set, OfficialsTable_RU _table, OffPositionStr_RU _position)
{
    OffIsOurGuy_RU origIsOurGuy;

    if (_set)
    {
        origIsOurGuy = _table.isOurGuy();
        positionLookup.setPosition(_table, _position);
        if (origIsOurGuy != _table.isOurGuy())
        {
            _table.Name = '';
            _table.Title = '';
        }
    }

    return positionLookup.valueToLabel(_table.Position);
}

