void setRelation_Account(OffTableAll_RU      _oldSel,
                         OffTableAll_RU      _newSel,
                         OfficialsTable_RU   _officials)
{
    if (_newSel != _oldSel || _newSel == OffTableAll_RU::All)
    {
        _officials.CustVendRelation = '';
        if (! _officials.isOurGuy())
        {
            _officials.Name = '';
            _officials.Title = '';
        }
    }
}

void setReportTypeRangeValue(FormDataSource _formDataSource, OffReportType_RU _reportType)
{
    QueryBuildRange reportTypeQBR;

    reportTypeQBR = findOrCreateRange_W(_formDataSource.query().dataSourceTable(tableNum(OfficialsTable_RU)), fieldNum(OfficialsTable_RU, ReportType));

    reportTypeQBR.value(queryValue(_reportType));
}

