public void lookup(FormControl _formControl, str _filterStr)
{
    CustVendRel cust, vend;
    OffReportTypeInventLocation_RU offReportTypeInventLocation = any2Enum(inventLocationButton.selection());

    switch (offReportTypeInventLocation)
    {
        case OffReportTypeInventLocation_RU::SalesInvoiceM15  :
            cust = officialsTableInventLocation.CustVendRelation;
            break;
        case OffReportTypeInventLocation_RU::PurchM4M7        :
            vend = officialsTableInventLocation.CustVendRelation;
            break;
        default:
            cust = this.getDefaultReportTypeInventLocationCustRelation(offReportTypeInventLocation);
            vend = this.getDefaultReportTypeInventLocationVendRelation(offReportTypeInventLocation);
            break;
    }

    element.nameLookup(this.datasource(), _formControl, cust, vend);
}

public void modified()
{
    NoYes isCust;

    super();

    OffReportTypeInventLocation_RU offReportTypeInventLocation = any2Enum(inventLocationButton.selection());

    switch (offReportTypeInventLocation)
    {
        case OffReportTypeInventLocation_RU::SalesInvoiceM15  :
            isCust = NoYes::Yes;
            break;
        case OffReportTypeInventLocation_RU::PurchM4M7        :
            isCust = NoYes::No;
            break;
        default:
            isCust = this.getDefaultReportTypeInventLocationIsCustomer(offReportTypeInventLocation);
            break;
    }

    element.nameModified(this.datasource(), isCust);
}

