[Form]
public class PCPriceModel extends FormRun
{
    PCClass                         selectedComponent;
    PCProductModelDetailsController controller;
    PCComponentTree                 componentTree;
    RefRecId                        priceModelRecId;
    RefRecId                        priceModelCurrency;
    RefRecId                        priceModelCurrencyDefault;
    CurrencyCode                    defaultCurrencyCode;
    PCProductConfigurationModel     productConfigurationModel;
}

protected PCPriceExpressionValue createPriceRule(PCPriceExpressionValue _expressionValue, PCPriceElement _priceElement, PCPriceExpression _priceExpression)
{
    _expressionValue.PriceExpression    = _priceExpression;
    _expressionValue.PriceElement       = _priceElement.RecId;
    _expressionValue.PriceModelCurrency = priceModelCurrency;

    _expressionValue.write();

    return _expressionValue;
}

public PCRuntimePriceModelInstance getRuntimePriceModelInstance()
{
    PCRuntimePriceModelInstance runtimePriceModelInstance = PCRuntimePriceModelInstance::construct();

    runtimePriceModelInstance.parmCurrency(selectedPriceModelCurrency.text());
    runtimePriceModelInstance.parmPriceModel(priceModelRecId);
    runtimePriceModelInstance.parmPriceModelCurrency(selectedPriceModelCurrency.text());

    return runtimePriceModelInstance;
}

    public void init()
    {
        Args            args = element.args();
        PCPriceModel    priceModel;

        if ((args == null) || (args.record() == null)  || !(args.record() is PCPriceModel))
        {
            throw error(Error::missingFormActiveBuffer(this.form().name()));
        }

        defaultCurrencyCode         = EcoResProductParameters::getDefaultCurrency();
        priceModel                  = args.record() as PCPriceModel;
        productConfigurationModel   =  PCProductConfigurationModel::find(priceModel.ProductConfigurationModel);

        controller                  = PCProductModelDetailsController::construct();
        controller.parmProductConfigurationModel(productConfigurationModel);

        selectedComponent           = controller.parmProductConfigurationModel().getRootComponent();
        priceModelRecId             = priceModel.RecId;
        priceModelCurrency          = PCPriceModelCurrency::findByPriceModelAndCurrency(priceModelRecId, defaultCurrencyCode).RecId;
        priceModelCurrencyDefault   = priceModelCurrency;

        super();

        this.design().caption(priceModel.Name);
        selectedPriceModelCurrency.text(defaultCurrencyCode);
        defaultPriceModelCurrency.text(defaultCurrencyCode);

        element.refreshControlsState(defaultCurrencyCode);

        //initialize the tree
        componentTree = PCComponentTree::newFromTreeComponent(pvmTree, selectedComponent);
        componentTree.initTree();
        componentTree.redrawTree();

        this.ViewEditModeHelper().EditModeSwitched += eventhandler(element.ViewModeSwitched);
		this.ViewEditModeHelper().ViewModeSwitched += eventhandler(element.ViewModeSwitched);

		this.ViewModeSwitched();
    }

void ViewModeSwitched()
{
    var editMode = this.viewEditModeHelper().isInEditMode();
    EditBasePrice.enabled(editMode);
    EditExpression.enabled(editMode);
    EditCondition.enabled(editMode);
}

private void refrerPriceElementsForSelectedCurrency()
{
    PCPriceExpressionValueRule_ds.executeQuery();
    PCPriceExpressionRule_ds.executeQuery();
    PCPriceExpressionValueBasePrice_ds.executeQuery();
    pcPriceBasePrice_ds.executeQuery();
}

private void refreshControlsState(CurrencyCode _currency)
{
    CurrencyDeleteButton.enabled(_currency != defaultCurrencyCode);

    PCPriceExpressionValueBasePriceDefault_PriceExpression.visible(_currency != defaultCurrencyCode);
    PCPriceExpressionValueRuleDefault_PriceExpression.visible(_currency != defaultCurrencyCode);

    ButtonGroup.enabled(_currency == defaultCurrencyCode);

    PCPriceExpressionRule_Name.allowEdit(_currency == defaultCurrencyCode);
    PCPriceExpressionRule_Condition.allowEdit(_currency == defaultCurrencyCode);

    PCPriceExpressionRule_ds.allowCreate(_currency == defaultCurrencyCode);
    PCPriceExpressionRule_ds.allowDelete(_currency == defaultCurrencyCode);
}

public void savePriceRule(PCPriceExpressionValue _expressionValue, PCPriceElement _priceElement, PCPriceExpression _priceExpression)
{
    if (_expressionValue.RecId == 0)
    {
        _expressionValue = this.createPriceRule(_expressionValue, _priceElement, _priceExpression);
    }
}

public PCPriceExpressionValue savePriceRuleAndPersist(PCPriceExpressionValue _expressionValue, PCPriceElement _priceElement, PCPriceExpression _priceExpression)
{
    _expressionValue = PCPriceExpressionValue::find(_expressionValue.RecId, true);

    ttsBegin;
    _expressionValue = this.createPriceRule(_expressionValue, _priceElement, _priceExpression);
    ttsCommit;

    return _expressionValue;
}

public void selectPriceModelCurrency(CurrencyCode _currencyCode)
{
    if (Currency::exist(_currencyCode))
    {
        priceModelCurrency = PCPriceModelCurrency::findOrCreateCurrency(priceModelRecId, _currencyCode).RecId;

        selectedPriceModelCurrency.text(_currencyCode);

        element.refrerPriceElementsForSelectedCurrency();

        element.refreshControlsState(_currencyCode);
    }
}

public void init()
{
    Args                        args = element.args();
    QueryBuildDataSource        qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(PCProductConfigurationModel));
    qbds.addRange(fieldNum(PCProductConfigurationModel, RecId)).value(queryValue(productConfigurationModel.RecId));
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(PCClass));
    qbds.clearDynalinks();
    qbds.clearRanges();
    qbds.addRange(fieldNum(PCClass, RecId)).value(queryValue(selectedComponent.RecId));
}

public void executeQuery()
{
    QueryBuildDataSource qbds;

    qbds = this.query().dataSourceTable(tableNum(PCClass));
    qbds.clearRanges();
    qbds.addRange(fieldNum(PCClass, RecId)).value(queryValue(selectedComponent.RecId));

    super();

    // Due to the unit of work mpde enabled for this form,
    // we need to explicitly create a PCClassPriceCalculation record, so the proper foreign key is set on the price elements
    PCClassPriceCalculation.write();
}

