void clicked()
{
    #PC
    str focusOnThisComponentFormat = #ComponentFocus;
    PCRuntimeConfiguratorValidate runtimeConfiguratorValidate = PCRuntimeConfiguratorValidate::construct();

    runtimeConfiguratorValidate.validateSubModel(
        pcProductConfigurationModel,
        PCProductModelDetailsController::getCommonComponent(componentTree.getSelectedPath()),
        PCPriceMethod::Attribute,
        element.getRuntimePriceModelInstance());

    super();
}

public void selectionChanged(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    super(_oldItem, _newItem, _how);

    if (componentTree.selectionChanged(_oldItem, _newItem, _how))
    {
        selectedComponent = componentTree.getSelectedComponent();
        pcClass_DS.executeQuery();
    }
}

public void lookup()
{
    SysTableLookup  lookup = SysTableLookup::newParameters(tableNum(PCPriceModelCurrency), this);
    Query           query = new Query();

    query.addDataSource(tableNum(PCPriceModelCurrency)).addRange(fieldNum(PCPriceModelCurrency, priceModel)).value(queryValue(PriceModelRecId));

    lookup.parmQuery(query);
    lookup.addLookupfield(fieldNum(PCPriceModelCurrency, Currency),true);

    lookup.performFormLookup();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.selectPriceModelCurrency(this.text());

    return ret;
}

void clicked()
{
    super();

    if (Box::yesNo("@SYS4001984", DialogButton::No) == DialogButton::Yes)
    {
        PCPriceModel::deleteAllPriceCalculationInCurrency(priceModelRecId, selectedPriceModelCurrency.text());

        element.selectPriceModelCurrency(EcoResProductParameters::find().Currency);
    }
}

        public void clicked()
        {
            super();
            PCPriceBasePrice_ds.leaveRecord(true);

			element.savePriceRule(PCPriceExpressionValueBasePrice, PCPriceBasePrice,'');

            controller.openEditorUsingStringControlData( PCClass_BasePrice,
                                                         '',
                                                         PCConstraintEditorOpenPurpose::EnterCalculation,
                                                         PCPriceMethod::Attribute,
                                                         element.getRuntimePriceModelInstance(),
                                                         componentTree.getSelectedPath());
            PCPriceBasePrice_ds.reread();
        }

void clicked()
{
    PCRuntimeConfiguratorValidate runtimeConfiguratorValidate = PCRuntimeConfiguratorValidate::construct();

    if (PCPriceExpressionRule)
    {
        runtimeConfiguratorValidate.validatePriceCalculationInSub(
            productConfigurationModel,
            PCProductModelDetailsController::getCommonComponent(componentTree.getSelectedPath()),
            PCPriceExpressionRule,
            element.getRuntimePriceModelInstance());
    }
    super();
}

        public void clicked()
        {
            super();
            boolean                 hasDummyPriceExpression;
            PCConstraintExpression  originalExpression = PCPriceExpressionValueRule.PriceExpression;

			PCPriceExpressionRule_Ds.leaveRecord(true);
			if (PCPriceExpressionValueRule.PriceExpression == '')
			{
				PCPriceExpressionValueRule.data(element.savePriceRuleAndPersist(PCPriceExpressionValueRule, PCPriceExpressionRule, '0'));

				hasDummyPriceExpression = true;
			}
            controller.openEditorUsingStringControlData(PCPriceExpressionRule_Condition,
																'',
																PCConstraintEditorOpenPurpose::EnterCondition,
																PCPriceMethod::Attribute,
																element.getRuntimePriceModelInstance(),
																componentTree.getSelectedPath());
			if (hasDummyPriceExpression)
			{
				element.savePriceRuleAndPersist(PCPriceExpressionValueRule, PCPriceExpressionRule, originalExpression);
				PCPriceExpressionValueRule_Ds.reread();
			}
        }

        public void clicked()
        {
            super();
            PCPriceExpressionRule_Ds.leaveRecord(true);

			element.savePriceRule(PCPriceExpressionValueRule, PCPriceExpressionRule, '');

            controller.openEditorUsingStringControlData(PCPriceExpressionRule_Expression,
                                                        '',
                                                        PCConstraintEditorOpenPurpose::EnterCalculation,
                                                        PCPriceMethod::Attribute,
                                                        element.getRuntimePriceModelInstance(),
                                                        componentTree.getSelectedPath());
            PCPriceExpressionRule_ds.research(true);
			// Repeat call to avoid that the line appears twice in the expresion grid
			PCPriceExpressionRule_ds.research(true);
        }

