/// <summary>
/// Edit or create the configuration nomenclature.
/// </summary>
public void editConfigurationNomenclature()
{
    PCConfigurationNomenclature nomenclature;

    if (!PCComponentConfigurationNomenclature || !PCComponentConfigurationNomenclature.PCConfigurationNomenclature)
    {
        if (Box::yesNo("@SCM:NoNomenclatureDefined", DialogButton::Yes) == DialogButton::Yes)
        {
            ttsbegin;

            nomenclature.Name = PCClass.Name;
            nomenclature.insert();

            PCComponentConfigurationNomenclature.NomenclatureRole = EcoResNomenclatureRole::Id;
            PCComponentConfigurationNomenclature.PCClass = PCClass.RecId;
            PCComponentConfigurationNomenclature.PCConfigurationNomenclature = nomenclature.RecId;
            PCComponentConfigurationNomenclature.write();

            ttscommit;
        }
    }
    else
    {
        nomenclature = PCConfigurationNomenclature::find(PCComponentConfigurationNomenclature.PCConfigurationNomenclature);
    }

    if (nomenclature)
    {
        nomenclature.editInSimpleForm(PCClass, this);
    }
}

/// <summary>
/// Edits a modifier condition for a component control.
/// </summary>
/// <param name="_set">
/// A Boolean value determining whether to set the current expression.
/// </param>
/// <param name="_componentControl">
/// The component control record the condition is associated with.
/// </param>
/// <param name="_expression">
/// The expression.
/// </param>
/// <param name="_modifierConditions">
/// A map to store the tentative edits before committing to database.
/// </param>
/// <returns>
/// The current expression.
/// </returns>
public PCConstraintExpression editModifierCondition(
    boolean                 _set,
    Common                  _componentControl,
    PCConstraintExpression  _expression,
    Map                     _modifierConditions)
{
    anytype modifier = modifierFactory.getComponentControlModifier(_componentControl);

    if (_set)
    {
        _modifierConditions.insert(modifier, _expression);
        // For existing records make sure edit is persisted (since componentControl might not be marked as dirty)
        if (_componentControl.RecId)
        {
            element.saveModifierCondition(_componentControl, _modifierConditions);
        }
    }
    else
    {
        if (!_modifierConditions.exists(modifier))
        {
            _modifierConditions.insert(modifier, modifierFactory.findModifierConditionExpression(_componentControl).Expression);
        }
        _expression = _modifierConditions.lookup(modifier);
    }

    return _expression;
}

/// <summary>
/// Toggles the include in reuse column for attributes based on the component reuse setting.
/// </summary>
public void enableAttributeIncludeInReuse()
