    attributeIncludeInReuse.visible(pcClass.ReuseEnabled == PCComponentConfigurationReuseType::Yes);
}

/// <summary>
/// Validates the form's arguments and initializes the form.
/// </summary>
/// <exception cref="Exception::Error">
/// Throws an exception is the form is launched with wrong arguments.
/// </exception>
public void init()
{
    PCProductConfigurationModel     argsModel;
    Args                            args            =  element.args();
    int                             counter         = 0;
    SecurityRights                  rights          = SecurityRights::newUser(curUserId());
    boolean                         hasRouteAccess;

    modifierFactory = PCModifierFactory::construct();
    attributeModifierConditions         = new Map(Types::Enum, Types::String);
    subComponentModifierConditions      = new Map(Types::Enum, Types::String);
    userRequirementModifierConditions   = new Map(Types::Enum, Types::String);

    //validating the caller
    if ((args == null) || (args.record() == null) || !args.caller())
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }

    // set the system language ID
    systemLanguage = SystemParameters::getSystemLanguageId();

    argsModel           = args.record() as PCProductConfigurationModel;
    selectedComponent   = argsModel.getRootComponent();
    controller          = PCProductModelDetailsController::construct();
    controller.parmProductConfigurationModel(argsModel);
    super();

    pcProductConfigurationModel_DS.executeQuery();
    pcClass_DS.reread();

    this.design().caption(argsModel.Name);
    ModelDescription.text(argsModel.editDescription(false, ''));

    //initialize the tree
    componentTree = PCComponentTree::newFromTreeComponent(pvmTree, selectedComponent);
    componentTree.initTree();
    componentTree.redrawTree();

    //apply security to Route grid based on the Delete (route) button as the table itself is reused for
    //other purposes (as the BOM grid) and could not be secured directly
    hasRouteAccess = rights.formControlAccessRight(this.form().name(), pcTemplateComponent_RouteDelete.name()) >= AccessRight::Edit;
    pcTemplateComponent_RouteGrid.enabled(hasRouteAccess);

    this.ViewEditModeHelper().EditModeSwitched += eventhandler(element.ViewModeSwitched);
    this.ViewEditModeHelper().ViewModeSwitched += eventhandler(element.ViewModeSwitched);

    this.ViewModeSwitched();
}

void ViewModeSwitched()
{
    var editMode = this.viewEditModeHelper().isInEditMode();

    boolean isTableConstraint = (pcConstraint.getInstanceRelationType() == tableStr(PCTableConstraint));

    EditTableConstraintDefinition.enabled(editMode && isTableConstraint);
    EditConstraintExpression.enabled(editMode && !isTableConstraint);

    EditConditionHidden.enabled(editMode);
    EditConditionMandatory.enabled(editMode);
    EditConditionReadOnly.enabled(editMode);
    EditExpression.enabled(editMode);
    EditSubComponentConditionHidden.enabled(editMode);
    EditUserRequirementConditionHidden.enabled(editMode);
    Edit_BOMGrid_Condition.enabled(editMode);
    Edit_RouteCondition.enabled(editMode);
}

/// <summary>
/// Opens the <c>PCCreateProductConfigurationModel</c> form.
/// </summary>
/// <param name="_editMode">
/// true if the form should be opened in edit mode; otherwise, false.
/// </param>
public void openNewProductModelForm(boolean _editMode)
{
    PCProductConfigurationModel model       = null;
    boolean                     formModal   = false;

    if (_editMode)
    {
        model = pcProductConfigurationModel;
        formModal = true;
    }

    PCModelingLibrary::loadForm(formStr(PCCreateProductConfigurationModel),model, this, true, formModal);

    if (_editMode)
    {
        pcProductConfigurationModel_DS.research(true);
    }
}

