/// <summary>
/// Saves the condition for the modifier.
/// </summary>
/// <param name="_componentControl">
/// The modifier.
/// </param>
/// <param name="_modifierConditions">
/// A map of modifier types to the conditions.
/// </param>
public void saveModifierCondition(Common _componentControl, Map _modifierConditions)
{
    anytype modifier = modifierFactory.getComponentControlModifier(_componentControl);

    if (_modifierConditions.exists(modifier))
    {
        modifierFactory.updateOrCreateModifierConstraint(_componentControl, _modifierConditions.lookup(modifier));
    }
}

