public boolean validateWrite()
{
    EcoResAttributeType        domain;
    AttributeValueText  valueText;
    boolean             ret;

    ret = super();

    if (ret && ecoResAttributeDefaultValue.Value)
    {
        domain      = EcoResAttributeType::find(ecoResAttribute.AttributeType);
        valueText   = attributeDefaultValueControl.text();

        if (!PCModelingLibrary::validateAttributeTypeValue(domain, valueText))
        {
            ret = checkFailed(strFmt("@SYS32810", "@SYS40175"));
        }
    }

    return ret;
}

//// <summary>
/// Gets or sets the text value associated with the current default value definition.
/// </summary>
/// <param name="_set">
/// true if the text value is set; otherwise, false;
/// </param>
/// <param name="_value">
/// The new text value when the <paramref name="set"/> parameter is true.
/// </param>
/// <returns>
/// The current text value of the default value definition.
/// </returns>
/// <remarks>
/// This edit method is used in the <c>PCProductConfigurationModelDetails</c> form.
/// </remarks>
public edit AttributeValueText getSetDefaultValue(boolean _set, EcoResAttributeDefaultValue _defaultValue, AttributeValueText _value)
{
    EcoResTextValue             currentValue;
    EcoResAttributeType         domain;
    PCAttributeValueFormatter   attributeValueFormatter = PCAttributeValueFormatter::construct();

    domain = EcoResAttributeType::find(EcoResAttribute::find(_defaultValue.Attribute).AttributeType);

    if (_set)
    {
        if (_value)
        {
            // if the default value is just created it wouldn't have a reference to an EcoResAttribute record
            domain = EcoResAttributeType::find(ecoResAttribute.AttributeType);

            if (!PCModelingLibrary::validateAttributeTypeValue(domain, _value))
            {
                throw error(strFmt("@SYS32810", "@SYS40175"));
            }
            else
            {
                ttsbegin;
                //add / change value
                currentValue = EcoResTextValue::find(_defaultValue.Value, true);
                currentValue.TextValue = attributeValueFormatter.formatAttributeValuePersist(domain, _value);
                //insert or update
                currentValue.write();
                ttscommit;
                _defaultValue.Value = currentValue.RecId;
            }
        }
        return _value;
    }
    else
    {
        currentValue = EcoResTextValue::find(_defaultValue.Value, false);
        return attributeValueFormatter.formatAttributeValueDisplay(domain, currentValue.TextValue);
    }
}

