public void init()
{
    QueryBuildDataSource qbds;
    super();
    qbds = this.query().dataSourceName(identifierStr(PCTemplateComponent_BOM));
    qbds.addRange(fieldNum(PCTemplateComponent, Requirement)).value(queryValue(PCRequirement::ProductRequirement));
    qbds.addRange(fieldNum(PCTemplateComponent, LegalEntity)).value(queryValue(CompanyInfo::current()));

    // PCTemplateComponent table is used in both "BOM lines" and "Route operations" datasources.
    // User may not edit BOM lines, if he has read-only access to PC models.
    // (He may potentially have write-access to route operations, though.)
    if (SecurityRights::construct().menuItemAccessRight(
            SecurableType::MenuItemDisplay,
            menuitemDisplayStr(PCProductConfigurationModelDetails)) <= AccessRight::View)
    {
        this.allowCreate(false);
        this.allowEdit(false);
        this.allowDelete(false);
    }
}

