public boolean validate()
{
    boolean ret;

    ret = super();

    ret = omlIdentifierLookup.validateCalculationTargetAttribute(pcCalculation.TargetAttribute);

    return ret;
}

public void initValue()
{
    super();

    PCComponentConfigurationNomenclature.NomenclatureRole = EcoResNomenclatureRole::Id;
}

public void init()
{
    super();

    QueryBuildRange nomenclatureRoleRange = this.queryBuildDataSource().addRange(fieldNum(PCComponentConfigurationNomenclature, NomenclatureRole));
    nomenclatureRoleRange.value(queryValue(EcoResNomenclatureRole::Id));
    nomenclatureRoleRange.status(RangeStatus::Hidden);
}

/// <summary>
/// Updates the SelectedListItem control in accordance to the currently selected item on the PVMTree control.
/// </summary>
/// <param name="_oldItem">
/// The item previously selected.
/// </param>
/// <param name="_newItem">
/// The new item selected
/// </param>
/// <param name="_how">
/// How the item got selected.
/// </param>
public void selectionChanged(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    super(_oldItem, _newItem, _how);

    if (componentTree.selectionChanged(_oldItem, _newItem, _how))
    {
        selectedComponent = componentTree.getSelectedComponent();
        pcClass_DS.executeQuery();
        element.reloadSelection();
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.enableAttributeIncludeInReuse();

    return ret;
}

public boolean modified()
{
    boolean ret = super();

    if (this.value())
    {
        element.editConfigurationNomenclature();
    }

    return ret;
}

