public void lookup()
{
    SysTableLookup              lookup;
    PCOMLIdentifierLookupTmp    omlIdentifierLookupTmp;

    super();

    omlIdentifierLookup.buildOmlLookupData();

    lookup = PCTableLookup::newParameters(tableNum(PCOMLIdentifierLookupTmp), this);
    lookup.addLookupfield(fieldNum(PCOMLIdentifierLookupTmp, omlIdentifier));
    omlIdentifierLookupTmp.setTmpData(omlIdentifierLookup.parmOmlIdentifierLookupTmp());
    select omlIdentifierLookupTmp;
    lookup.parmTmpBuffer(omlIdentifierLookupTmp);
    lookup.parmSkipApplicationOfFilter(true);
    lookup.performFormLookup();
}

void clicked()
{
    PCSubComponentRequirement subComponentRequirement;
    PCTemplateComponent templateComponent;
    PCSubComponent      activeSubComponent = pcSubComponent.data();

    // commit changes
    pcSubComponent_DS.leaveRecord(true);
    // re-select the commit record
    pcSubComponent_DS.positionToRecord(activeSubComponent);

    if (!pcSubComponent.RecId)
    {
        throw error("@SYS94694");
    }
    select firstonly RecId from subComponentRequirement
    where   subComponentRequirement.SubComponent == pcSubComponent.RecId
    join    templateComponent
    where   templateComponent.RecId == subComponentRequirement.TemplateComponent
    &&      templateComponent.LegalEntity == CompanyInfo::current()
    &&      templateComponent.ComponentClass == pcClass.RecId;

    if (!templateComponent)
    {
        templateComponent = PCModelingLibrary::createSubComponentRequirements(pcSubComponent);
    }

    PCModelingLibrary::loadForm(formStr(PCBOMLineDetails), templateComponent, this, true, true);

    //Refresh item number
    pcSubComponent_DS.reread();
    pcSubComponent_DS.refresh();
}

