[Form]
public class PDSBalanceProdBOMEnhanced extends FormRun
{
    PDSBalanceProdBOMEnhanced           balancer;
    QueryBuildRange                     qbrProdId;

    InventDimCtrl_Frm_BatchBalancing    inventDimFormSetupDetail;
    boolean                             allowBatchBalancingForBOMLines;

    private static boolean isPDSBalanceProdBOMHigherThan100PotencyAttributeValueFixFlightEnabled = PDSBalanceProdBOMHigherThan100PotencyAttributeValueFixFlight::instance().isEnabled();

}

public void init()
{
    if (element.args().caller() && element.args().record().TableId != tablenum(ProdTable))
    {
        throw error(Error::missingFormActiveBuffer(element.name(), tableStr(ProdTable)));
    }

    balancer  = PDSBalanceProdBOMEnhanced::construct();
    ProdTable prodTable = element.args().record();
    balancer.parmProdId(prodTable.ProdId);

    super();

    balancer.calcProdActiveQty();
    allowBatchBalancingForBOMLines = balancer.allowBatchBalancingForBOMLines();

    element.setButtonEnabled();
    element.updateDesign(InventDimFormDesignUpdate::Init);
    element.setPDSBatchBalanceConfirmButtonText(prodTable.ProdId);

    if (PDSBalanceProdBOMEnhancedSaveButtonFlight::instance().isEnabled())
    {
        #SysSystemDefinedButtons
        FormCommandButtonControl saveButton = this.control(this.controlId(#SystemDefinedSaveButton)) as FormCommandButtonControl;
        if (saveButton)
        {
            saveButton.enabled(false);
            saveButton.visible(false);
        }
    }
}

private void setPDSBatchBalanceConfirmButtonText(ProdId _prodId)
{
    // If there are only WHS enabled items, the button should be for release to warehouse.
    if (!ProdBOM::hasNonWHSItem(_prodId))
    {
        PDSBatchBalanceConfirm.text("@WAX:WHSMenuReleaseTowarehouseSubmenu");
        PDSBatchBalanceConfirm.helpText("@FieldDescriptions_WHS:ReleaseToWarehouseButtonBatchBalanceHelpText");
    }
    // If there are only non WHS enabled items, the button should be for confirming the formula. 
    else if (!ProdBOM::hasWHSItem(_prodId))
    {
        PDSBatchBalanceConfirm.text("@PSC65");
        PDSBatchBalanceConfirm.helpText("@FieldDescriptions_WHS:ConfirmFormulaButtonBatchBalanceHelpText");
    }
}

private object inventDimSetupObject()
{        
    return inventDimFormSetupDetail;
}

public void setButtonEnabled()
{
    BalanceIngredients.enabled(allowBatchBalancingForBOMLines && balancer.allowBatchBalancing());
    PDSBatchBalanceConfirm.enabled(balancer.allowBatchFirming());
}

public void setMatchedQty()
{
    balancer.setMatchedQty(ProdBOM);

    prodBOM_ds.refresh();
    element.setButtonEnabled();
}

public void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm       inventDimParmShowDetail;
    InventDimParm       inventDimParmEnabledDetail;
    InventDimParm       inventDimParmDisabledDetail;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetupDetail)
            {
                element.args().record(ProdBOM);
                inventDimFormSetupDetail  = InventDimCtrl_Frm_BatchBalancing::newFromForm(element);
            }
            inventDimParmShowDetail.initCovReqItemTable(InventDimGroupSetup::newItemId(ProdBOM.ItemId));
            inventDimParmShowDetail.InventBatchIdFlag = true;
            inventDimParmShowDetail.InventLocationIdFlag = true;
            inventDimParmDisabledDetail.initFromInventDimParm(inventDimParmShowDetail);
            inventDimFormSetupDetail.parmDimParmVisibleGrid(inventDimParmShowDetail);
            inventDimFormSetupDetail.parmDimParmLockedRightClick(inventDimParmDisabledDetail);
            //Fall through
        case InventDimFormDesignUpdate::Active:
            inventDimParmEnabledDetail.initFromInventDim(InventDim);
            inventDimFormSetupDetail.parmDimParmEnabled(inventDimParmEnabledDetail);
            inventDimFormSetupDetail.formSetControls(true);
            break;

        default:
            throw error(strFmt("@SYS54195",funcName()));
    }
}

void rightClickModifyQuery()
{
    element.inventDimSetupObject().parmDimParmVisible(element.inventDimSetupObject().parmDimParmVisibleGrid());
    element.inventDimSetupObject().rightClickModifyQueryGroupBy(inventSum_DS);  

    inventSum_DS.executeQuery();

    if (PDSBalanceProdBOMEnhancedRemainActiveFlight::instance().isEnabled())
    {
        balancer.clearState();
    }
}

public display PDSBalancedQty prodBOMBalQty(ProdBOM _prodBOM)
{
    return balancer.bomBalanceQty(_prodBOM.InventTransId);
}

public display PDSActiveQty activeQty(ProdBOM _prodBOM)
{
    return balancer.bomActiveQty(_prodBOM.InventTransId);
}

public int active()
{
    int ret = super();

    balancer.parmBaseAttributeId(InventTable::pdsFindBaseAttribute(prodBOM.ItemId).PdsBatchAttribRelation);

    element.updateDesign(InventDimFormDesignUpdate::Active);

    if (prodBom.PDSIngredientType == PDSIngredientTypeEnum::Active)
    {
        element.setMatchedQty();
    }

    return ret;
}

public void executeQuery()
{
    qbrProdId.value(balancer.parmProdId());
    super();
}

public void init()
{
    super();
    qbrProdId = ProdBOM_ds.query().dataSourceTable(tablenum(ProdBOM)).addRange(fieldnum(ProdBOM, ProdId));
}

public edit PDSActiveQtyUsed activeQtyUsed(boolean _set, InventSum _sum, PDSActiveQtyUsed _qty)
{
    PDSQtyUsed retQty = 0;

    if (_set)
    {
        retQty = balancer.setActiveQtyUsed(ProdBOM, _sum, _qty);
        if (_qty > 0)
        {
            element.setMatchedQty();
        }
    }
    else
    {
        retQty = balancer.activeQtyUsed(ProdBOM, _sum);
    }

    return retQty;
}

public display PDSQtyUsed qtyUsed(InventSum _sum)
{
    return balancer.qtyUsed(ProdBOM, _sum);
}

public void init()
{
    super();

    QueryBuildDataSource inventSumDs = inventSum_DS.query().dataSourceTable(tableNum(InventSum));
    InventSum::queryAddSumFields(inventSumDs);
}

public edit Qty qtyMarked(boolean _set, InventSum _sum, InventQty _qty)
{
    Qty ret;

    if (_set)
    {
        ret = balancer.setQtyMarked(ProdBOM, _sum, _qty);
        element.setMatchedQty();
    }
    else
    {
        if (isPDSBalanceProdBOMHigherThan100PotencyAttributeValueFixFlightEnabled)
        {
            PDSBaseValueNum baseAttributeValue = str2num(balancer.baseAttributeValue(_sum));
            ret = balancer.qtyUsed(ProdBOM, _sum, _sum.AvailPhysical) * (baseAttributeValue / 100);
        }
        else
        {
            ret = balancer.qtyUsed(ProdBOM, _sum, _sum.AvailPhysical);
        }
    }

    return ret;
}

public edit boolean markSum(boolean _set, InventSum _sum, boolean _marked)
{
    if (_set)
    {
        if (_marked)
        {
            if (this.baseAttributeValue(_sum) == '')
            {
                throw error("@PSC669");
            }

            Qty qty;
            if (isPDSBalanceProdBOMHigherThan100PotencyAttributeValueFixFlightEnabled)
            {
                PDSBaseValueNum baseAttributeValue = str2num(balancer.baseAttributeValue(_sum));
                qty = balancer.qtyUsed(ProdBOM, _sum, _sum.AvailPhysical) * (baseAttributeValue / 100);
            }
            else
            {
                qty = balancer.qtyUsed(ProdBOM, _sum, _sum.AvailPhysical);
            }

            this.qtyMarked(true, _sum, qty);
        }
        else
        {
            balancer.removeMark(ProdBOM, _sum);
        }

        element.setMatchedQty();

        return _marked;
    }

    return balancer.isMarked(ProdBOM, _sum);
}

public display PdsBatchAttribValue baseAttributeValue(InventSum _sum)
{
    return balancer.baseAttributeValue(_sum);
}

public display PDSActiveQtyAvail activeBatchQty(InventSum _sum)
