    return balancer.activeBatchQty(_sum);
}

public void executeQuery()
{
    element.inventDimSetupObject().modifyQuery(inventSum_DS, inventDim_DS);
    super();
}

public void clicked()
{
    balancer.balanceBatch();
    element.setButtonEnabled();
    prodBOM_ds.research();
}

public void clicked()
{
    Args                 args = new Args(this.menuFunction().object());
    PDSTmpBatchSelection buffer;
    FormRun              callerFormRun;

    if (!balancer.isBatchBalanced())
    {
        checkFailed("@PSC322");
        return;
    }

    balancer.copyMarkToBuffer(buffer);

    args.record(buffer);
    args.parmObject(balancer.bomBalanceQuantities());
    args.parm(prodBOM.ProdId);
    args.caller(element);
    this.menuFunction().run(args);

    if (!ProdTable::find(balancer.parmProdId()).statusClient().pdsCanBeBatchBalanced())
    {
        callerformRun = element.args().callerFormControl().formRun();
        if (callerformRun.pageInteraction())
        {
            callerformRun.pageInteraction().page().actionPaneControlEnabled(formControlStr(ProdTableListPage, PDSBatchBalancing), false);
        }
        else if (element.args().caller() && formHasMethod(element.args().caller(), identifierStr(enableControlsActive)))
        {
            element.args().caller().enableControlsActive();
        }

        element.close();
    }
}
