void activeControls()
{
    boolean isPosted;
    boolean disableProjTaskAddition;
    boolean disableProjTaskDeletion;
    boolean hasPostedTrans = psaContractLineItems.hasPostedHistory();
    boolean showSetupCategories;

    if (enableMultipleContractLinesForProject)
    {
        isPosted = element.disableProjDeletionButtonWhenFeatureEnabled();
        disableProjTaskAddition = false;
        disableProjTaskDeletion = isPosted;
        showSetupCategories = psaContractLineItems.OverrideChargeableCategories;
    }
    else
    {
        isPosted = hasPostedTrans;
        disableProjTaskAddition = isPosted;
        disableProjTaskDeletion = isPosted;
        showSetupCategories = true;
    }

    psaContractLineItems_LineValue.allowEdit(true);
    psaContractLineItems_Units.allowEdit(false);
    psaContractLineItems_UOM.allowEdit(false);
    psaContractLineItems_UnitPrice.allowEdit(false);

    psaContractLineItems_FeeProjId.enabled(true);
    psaContractLineItems_FeeCategoryId.enabled(true);
    psaContractLineItems_FeePercentage.enabled(false);
    psaContractLineItems_IncludeIndirectCost.enabled(false);
    psaContractLineItems_SalesTaxGroup.enabled(true);
    psaContractLineItems_ds.object(fieldNum(PSAContractLineItems, FeeProjId)).mandatory(true);
    psaContractLineItems_ds.object(fieldNum(PSAContractLineItems, FeeCategoryId)).mandatory(!psaContractLineItems.isBillingRuleForFixedPriceProject());

    switch (psaContractLineItems.LineType)
    {
        case PSAContractLineType::LumpSum:
            milestoneDetails.visible(true);
            projectTask.visible(false);
            contractLines.visible(false);
            psaContractLineItems_LineValue.allowEdit(false);
            psaContractLineItems_ds.object(fieldNum(PSAContractLineItems, FeeProjId)).mandatory(false);
            psaContractLineItems_ds.object(fieldNum(PSAContractLineItems, FeeCategoryId)).mandatory(false);
            psaContractLineItems_FeeProjId.enabled(false);
            psaContractLineItems_FeeCategoryId.enabled(false);
            psaContractLineItems_SalesTaxGroup.enabled(false);
            tabFinancialDimensions.visible(false);
            element.updateTransTypeControls(false);
            break;

        case PSAContractLineType::TAndM:
            milestoneDetails.visible(false);
            projectTask.visible(true);
            setupCategories.visible(showSetupCategories);
            contractLines.visible(false);
            psaContractLineItems_LineValue.allowEdit(false);
            psaContractLineItems_IncludeIndirectCost.enabled(true);
            psaContractLineItems_ds.object(fieldNum(PSAContractLineItems, FeeProjId)).mandatory(false);
            psaContractLineItems_ds.object(fieldNum(PSAContractLineItems, FeeCategoryId)).mandatory(false);
            psaContractLineItems_FeeProjId.enabled(false);
            psaContractLineItems_FeeCategoryId.enabled(false);
            psaContractLineItems_SalesTaxGroup.enabled(false);
            addProjTask.enabled(!disableProjTaskAddition);
            removeProjTask.enabled(!disableProjTaskDeletion);
            tabFinancialDimensions.visible(false);
            element.updateTransTypeControls(false);
            break;

        case PSAContractLineType::Progress:
            milestoneDetails.visible(false);
            projectTask.visible(true);
            setupCategories.visible(showSetupCategories);
            contractLines.visible(false);
            addProjTask.enabled(!disableProjTaskAddition);
            removeProjTask.enabled(!disableProjTaskDeletion);
            tabFinancialDimensions.visible(true);
            element.updateTransTypeControls(false);
            break;

        case PSAContractLineType::Fee:
            milestoneDetails.visible(false);
            contractLines.visible(true);
            projectTask.visible(false);
            psaContractLineItems_LineValue.allowEdit(false);
            psaContractLineItems_FeePercentage.enabled(true);
            addContractLines.enabled(!isPosted);
            removeContractLines.enabled(!isPosted);
            tabFinancialDimensions.visible(true);
            element.updateTransTypeControls(true);
            break;

        case PSAContractLineType::UOD:
            milestoneDetails.visible(false);
            projectTask.visible(false);
            contractLines.visible(false);
            psaContractLineItems_LineValue.allowEdit(false);
            psaContractLineItems_Units.allowEdit(true);
            psaContractLineItems_UOM.allowEdit(true);
            psaContractLineItems_UnitPrice.allowEdit(true);
            tabFinancialDimensions.visible(true);
            element.updateTransTypeControls(true);
            break;
    }

    ProjInvoiceTable projInvTable = m_tProjInvoiceTable ? m_tProjInvoiceTable : ProjInvoiceTable::find(psaContractLineItems.ProjInvoiceProjId);
    boolean lockFieldsForEditContracts = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::EditContracts) && Project::isContractExternallyManaged(projInvTable.ProjInvoiceProjId);

    if (hasPostedTrans)
    {
        psaContractLineItems_DS.allowEdit(false);
        psaContractLineItems_DS.allowDelete(false);
    }
    else
    {
        psaContractLineItems_DS.allowEdit(true);
        psaContractLineItems_DS.allowDelete(!lockFieldsForEditContracts);
    }

    psaContractLineItems_DS.allowCreate(!lockFieldsForEditContracts);
    AddMilestone.enabled(!lockFieldsForEditContracts);
    RemoveMilestone.enabled(!lockFieldsForEditContracts);
}

private void updateTransTypeControls(boolean _defaultFeeOnly)
{
    if (element.enableMultipleContractLinesForProject)
    {
        PSAContractLineItems_IncludeExpenseTrans.visible(!_defaultFeeOnly);
        PSAContractLineItems_IncludeHourTrans.visible(!_defaultFeeOnly);
        PSAContractLineItems_IncludeItemTrans.visible(!_defaultFeeOnly);
        // Default to check Fee type for Fee and UOD contract line
        PSAContractLineItems_IncludeFeeTrans.enabled(!_defaultFeeOnly);
        if (!PSAContractLineItems_IncludeFeeTrans.checked())
        {
            psaContractLineItems.IncludeFeeTrans = _defaultFeeOnly ? NoYes::Yes : NoYes::No;
        }
    }
}

/// <summary>
///     Returns a count of the <c>PSAContractLineItems</c> record that satisfy the below conditions -
///         1. The line type is not fee.
///         2. The value in the ProjInvoiceProjId column is same as the value in m_ProjInvoiceProjId.
/// </summary>
/// <returns>
///     Count of the <c>PSAContractLineItems</c> records that match the above conditions.
/// </returns>
protected int getContractLineItemsCount()
{
    int64 recordCount = (select count(RecId) from psaContractLineItems where psaContractLineItems.LineType != PSAContractLineType::Fee && psaContractLineItems.ProjInvoiceProjId == m_ProjInvoiceProjId).RecId;
    return int642int(recordCount);
}

void addContractLine()
{
    PSAContractLineItems    tpsaContractLineItems;
    container               cnData;

    if (psaContractLineItems.ContractLineNum)
    {
        if (psaContractLineItems_Right_DS.anyMarked())
        {
            tpsaContractLineItems = psaContractLineItems_Right_DS.getFirst(1);
            if (!tpsaContractLineItems)
            {
                cnData += psaContractLineItems_Right.ContractLineNum;
            }
            else
            {
                while (tpsaContractLineItems)
                {
                    cnData += tpsaContractLineItems.ContractLineNum;
                    tpsaContractLineItems = psaContractLineItems_Right_DS.getNext();
                }
            }
        }

        PSAContractLineItemsServer::addRefCLINToContractLines(cnData, psaContractLineItems.ContractLineNum);
        psaContractLineItems_DS.reread();
        psaContractLineItems_DS.refresh();
    }
}

void addProjectTask()
{
    ProjTable   tProjTable;
    container   cnData;

    if (psaContractLineItems.ContractLineNum)
    {
        if (projTable_Right_DS.anyMarked())
        {
            tProjTable = projTable_Right_DS.getFirst(1);
            if (!tProjTable)
            {
                cnData += projTable_Right.ProjId;
            }
            else
            {
                while (tProjTable)
                {
                    cnData += tProjTable.ProjId;
                    tProjTable = projTable_Right_DS.getNext();
                }
            }
        }

        PSAContractLineItemsServer::addProjToContractLines(cnData, psaContractLineItems.ContractLineNum);
        psaContractLineItems.IsCrossReference = NoYes::Yes;
    }
}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (!psaContractLineItems.RecId && ret)
    {
        PSAProjFundingSourceRetain::deleterecords(psaContractLineItems.ContractLineNum);
    }

    return ret;
}

public void close()
{
    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodClose();
    }

    super();

    element.refreshProjProjectContractsListPage();

    if (element.args().caller()   &&
        formHasMethod(element.args().caller(), literalStr('refreshBillingRules')))
    {
        element.args().caller().refreshBillingRules();
    }
}

