public void init()
{
    Args args = element.args();

    if (ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled())
    {
        FormRun caller = args.caller();
        if (caller.name() == formStr(ProjContractLinesPart))
        {
            PSAContractLineItems contractLineItems = caller.dataSource(formDataSourceStr(ProjContractLinesPart, PSAContractLineItems)).cursor();
            if (contractLineItems)
            {
                ProjectOperationsDWHelper dw = ProjectOperationsDWHelper::construct();
                dw.openProjectContractLineDetailsInCE(contractLineItems.ContractLineNum, contractLineItems.DataAreaId);
            }
        }

        this.close();
    }
    else
    {
        super();

        if (args.dataset())
        {
            switch (args.dataset())
            {
                case tableNum(ProjInvoiceTable):
                    ProjInvoiceTable projInvoiceTableLocal = args.record();
                    m_ProjInvoiceProjId = projInvoiceTableLocal.ProjInvoiceProjId;
                    break;

                case tableNum(PSAContractLineItems):
                    psaContractLineItems psaContractLineItemsLocal = args.record();
                    m_ProjInvoiceProjId = psaContractLineItemsLocal.ProjInvoiceProjId;
                    break;
            }
        }

        projectTask.visible(false);
        contractLines.visible(false);

        // <GTE>
        psaContractLineItems_SalesTaxGroup.visible(!TaxSolutionScopeIntegrationUtil::isCompanyEnabled());
        // </GTE>

        if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
        {
            psaContractLineItems_IncludeIndirectCost.visible(true);
        }

        // If multiple contract lines per project feature is enabled
        // change labels and captions from "Billing rule" to "Contract line"
        if (element.enableMultipleContractLinesForProject)
        {
            element.design().caption("@PSA2825");
            PSAContractLineItems_ContractLineNum1.label("@PSA2823");
            PSAContractLineItems_ContractLineNum3.label("@PSA2823");
            General.caption("@Proj:ContractLineDetails");
            GridPSAContractLineItems_LineType.label("@SYS9844");
            PSAContractLineItems_Right_ContractLineNum.label("@PSA2823");
            PSAContractLineItems_Left_ContractLineNum.label("@PSA2823");
        }

        PSAContractLineItems_BillingMethod.enabled(element.enableMultipleContractLinesForProject);
        PSAContractLineItems_BillingMethod.visible(element.enableMultipleContractLinesForProject);

        TransTypes.enabled(element.enableMultipleContractLinesForProject);
        TransTypes.visible(element.enableMultipleContractLinesForProject);

        PSAContractLineItems_Details.enabled(element.enableMultipleContractLinesForProject);
        PSAContractLineItems_Details.visible(element.enableMultipleContractLinesForProject);

        salesPriceCache = new Map(Types::String, Types::Real);
        element.initNumberSeqFormHandler();
    }
}

NumberSeqFormHandler initNumberSeqFormHandler()
{
    NumberSequenceTable numberSeqTableBillingRule;

    if (!numberSeqFormHandler)
    {
        numberSeqTableBillingRule = NumberSeqReference::findReference(extendedTypeNum(PSAContractLineNum)).numberSequenceTable();
        if (numberSeqTableBillingRule && numberSeqTableBillingRule.Manual == NoYes::No)
        {
            numberSeqFormHandler = NumberSeqFormHandler::newForm(numberSeqTableBillingRule.RecId,
                                                                 element,
                                                                 psaContractLineItems_DS,
                                                                 fieldNum(PSAContractLineItems, ContractLineNum));
        }
    }

    return numberSeqFormHandler;
}

