void refreshProjProjectContractsListPage()
{
    FormRun           formRun;
    ProjInvoiceTable  projInvoiceTable;

    Object callerForm = element.args().caller();

    formRun = element.args().caller();

    if (callerForm)
    {
        if (callerForm.name() == formStr(ProjProjectContractsListPage))
        {
            projInvoiceTable = element.args().record();

            formRun.dataSource().findRecord(projInvoiceTable);
        }
    }
}

