/// <summary>
/// Checks if there are any posted transaction for any of the selected projects to be removed.
/// </summary>
/// <returns>true if transactions exist; false otherwise.</returns>
boolean existPostedTransForAnySelectedProj()
{
    if (projTable_Left_DS.anyMarked())
    {
        ProjTable projTableLoc = projTable_Left_DS.getFirst(1);
        while (projTableLoc)
        {
            if (psaContractLineItems.hasPostedHistoryForProject(projTableLoc.ProjId))
            {
                return true;
            }
            projTableLoc = projTable_Left_DS.getNext();
        }
    }

    return false;
}

/// <summary>
/// Checks if there are any non-reversed estimates for any of the selected projects to be removed.
/// </summary>
/// <returns>true if estimates exist; false otherwise.</returns>
boolean existEstimatesForAnySelectedProj()
{
    if (projTable_Left_DS.anyMarked())
    {
        ProjTable projTableLoc = projTable_Left_DS.getFirst(1);
        while (projTableLoc)
        {
            ProjControlPeriodTable estimate;

            select firstonly RecId from estimate
                where estimate.ProjId == projTableLoc.ProjId && estimate.PeriodStatus != ProjControlPeriodStatus::Reversal;

            if (estimate.RecId)
            {
                return true;
            }
            projTableLoc = projTable_Left_DS.getNext();
        }
    }

    return false;
}

/// <summary>
/// Checks whether to disable project removal button when multiple contract line feature is enabled.
/// </summary>
/// <returns>true if project removal button should be disabled; false otherwise.</returns>
boolean disableProjDeletionButtonWhenFeatureEnabled()
{
    boolean hasPostedTrans = element.existPostedTransForAnySelectedProj();
    return hasPostedTrans || (psaContractLineItems.BillingMethod == ProjContractBillingMethod::FixedPrice && element.existEstimatesForAnySelectedProj());
}

void setEditAccessForFeeCategoryId()
{
    boolean isProgressOrUODForFPproj;

    isProgressOrUODForFPproj = psaContractLineItems.isBillingRuleForFixedPriceProject();

    if (isProgressOrUODForFPproj)
    {
        psaContractLineItems_FeeCategoryId.enabled(false);
        psaContractLineItems.FeeCategoryId = '';
    }
    else
    {
        psaContractLineItems_FeeCategoryId.enabled(true);
    }

    psaContractLineItems_ds.object(fieldNum(PSAContractLineItems, FeeCategoryId)).mandatory(!isProgressOrUODForFPproj);
}

void setEditAccessForMilestoneTransaction()
{
    boolean allowEditMilestoneTransaction;
    boolean allowEditCurrency;

    allowEditMilestoneTransaction = (projOnAccTrans.proposal())? false: true;
    projOnAccTrans_ds.allowEdit(allowEditMilestoneTransaction);
    projOnAccTrans_ds.allowDelete(allowEditMilestoneTransaction);

    if (allowEditMilestoneTransaction)
    {
        allowEditCurrency = !ProjInvoiceTable::isSalesCurrencyLocked(psaContractLineItems.ProjInvoiceProjId);
        projOnAccTrans_ds.object(fieldNum(ProjOnAccTrans, CurrencyId)).allowEdit(allowEditCurrency);
        projOnAccTrans_ds.object(fieldNum(ProjOnAccTrans, CurrencyId)).skip(!allowEditCurrency);
    }
}

void setTaxWithholdEnabled_IN()
{
    psaContractLineItemsTaxExtensionIN_ds.object(fieldNum(PSAContractLineItemsTaxExtensionIN, tdsGroup)).enabled(!PSAContractLineItemsTaxExtensionIN.tcsGroup);
    psaContractLineItemsTaxExtensionIN_ds.object(fieldNum(PSAContractLineItemsTaxExtensionIN, tcsGroup)).enabled(!PSAContractLineItemsTaxExtensionIN.tdsGroup);
}

public void setVisibleTaxInformation_IN()
{
    TaxParameters               taxParametersLoc                = TaxParameters::find();
    boolean                     isTaxParametersMarked           = TaxParameters::checkTaxParameters_IN();
    boolean                     isTaxWithholdParametersMarked   = TaxWithholdParameters_IN::checkTaxParameters();
    TaxWithholdParameters_IN    taxWithholdParameters           = TaxWithholdParameters_IN::find();

    PSAContractLineType     contractLineType;

    contractLineType = psaContractLineItems.LineType;
    if ( isTaxParametersMarked && (contractLineType == PSAContractLineType::UOD ||
                                   contractLineType == PSAContractLineType::Fee ||
                                   contractLineType == PSAContractLineType::Progress ))
    {
        assessableValue.visible(isTaxParametersMarked);
        maxRetailPrice.visible(isTaxParametersMarked);
        psaContractLineItemsTaxExtensionIN_ds.object(fieldNum(PSAContractLineItemsTaxExtensionIN, tdsGroup)).visible(taxWithholdParameters.tdsActivate);
        psaContractLineItemsTaxExtensionIN_ds.object(fieldNum(PSAContractLineItemsTaxExtensionIN, tcsGroup)).visible(taxWithholdParameters.tcsActivate);
    }
    else
    {
        assessableValue.visible(false);
        maxRetailPrice.visible(false);
        psaContractLineItemsTaxExtensionIN_ds.object(fieldNum(PSAContractLineItemsTaxExtensionIN, tdsGroup)).visible(false);
        psaContractLineItemsTaxExtensionIN_ds.object(fieldNum(PSAContractLineItemsTaxExtensionIN, tcsGroup)).visible(false);
    }
}

/// <summary>
/// When <c>ProjEnableMultipleContractLinesForProjectFeature</c> is enabled, limit the possible selections for LineType based on the selected BillingMethod
/// </summary>
/// <param name = "_billingMethod">Selected billing method.</param>
protected void filterLineTypeBasedOnBillingMethod(ProjContractBillingMethod _billingMethod)
{
    // clear all known enum entries from combo box to establish a known state
    PSAContractLineItems_LineType.delete(enum2Str(PSAContractLineType::LumpSum));
    PSAContractLineItems_LineType.delete(enum2Str(PSAContractLineType::Progress));
    PSAContractLineItems_LineType.delete(enum2Str(PSAContractLineType::TAndM));
    PSAContractLineItems_LineType.delete(enum2Str(PSAContractLineType::Fee));
    PSAContractLineItems_LineType.delete(enum2Str(PSAContractLineType::UOD));

    if (_billingMethod == ProjContractBillingMethod::FixedPrice)
    {
        PSAContractLineItems_LineType.add(enum2Str(PSAContractLineType::LumpSum));
    }
    else if (_billingMethod == ProjContractBillingMethod::TimeMaterial)
    {
        // We add all enums and then delete the lumpSum enum to maintain the proper order
        PSAContractLineItems_LineType.add(enum2Str(PSAContractLineType::LumpSum));
        PSAContractLineItems_LineType.add(enum2Str(PSAContractLineType::Progress));
        PSAContractLineItems_LineType.add(enum2Str(PSAContractLineType::TAndM));
        PSAContractLineItems_LineType.add(enum2Str(PSAContractLineType::Fee));
        PSAContractLineItems_LineType.add(enum2Str(PSAContractLineType::UOD));
        PSAContractLineItems_LineType.delete(enum2Str(PSAContractLineType::LumpSum));

        // If there are no contract line items then fee is not a valid choice
        int recordCount = element.getContractLineItemsCount();
        if (recordCount == 0)
        {
            PSAContractLineItems_LineType.delete(enum2Str(PSAContractLineType::Fee));
        }

    }
}

private boolean canTransactionTypeBeEnabled(str _contractLineNumber, str _contractId, projTransType _transType)
{
    if (ProjMultipleContractLinesForProjectFeatureHelper::existOverlappingContractLineForTransType(_contractLineNumber, _contractId, _transType))
    {
        ProjId projId = ProjMultipleContractLinesForProjectFeatureHelper::getAssociatedProjIdFromContractLineNum(_contractLineNumber);
        return checkFailed(strFmt("@Proj:OverlappingContractLineTransTypeUpdateError", enum2Str(_transType), _contractLineNumber, _contractId, projId));
    }

    return true;
}

public void create(boolean _append = false)
{
    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodDataSourceCreatePre();
    }

    super(_append);

    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodDataSourceCreate(true);
    }

    psaProjFundingSourceRetain.insertFromContract(m_ProjInvoiceProjId, psaContractLineItems.ContractLineNum);
}

public boolean validateWrite()
{
    boolean ret = super();

    if (psaContractLineItems.LineType == PSAContractLineType::Fee && ret)
    {
        int recordCount = element.getContractLineItemsCount();

        if (recordCount == 0)
        {
            ret = checkFailed("@PSA11562");
        }
    }

    if (ret && numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodDataSourceValidateWrite();
    }

    return ret;
}

public void delete()
{
    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodDataSourceDelete();
    }

    super();

    projectTask.visible(false);
    contractLines.visible(false);

    PSAContractLineItems::updateContractLine(m_ProjInvoiceProjId);
}

public int active()
{
    int ret;

    ret = super();

    if (psaContractLineItems.ContractLineNum)
    {
        m_qbrProjTaskLeftLineNumber.value(psaContractLineItems.ContractLineNum);
        m_qbrProjTaskLeftCategory.value(Global::queryValue(''));
        m_qbrProjTaskLeftActiveAll.value(Global::queryValue(SysQueryRangeUtilProject::projActiveAll(ProjActiveAll::Active)));
        m_qbrProjTaskRightLineNumber.value(psaContractLineItems.ContractLineNum);
        m_qbrProjTaskRightProjInvoiceProjId.value(Global::queryValue(psaContractLineItems.ProjInvoiceProjId));
        m_qbrProjTaskRightActiveAll.value(Global::queryValue(SysQueryRangeUtilProject::projActiveAll(ProjActiveAll::Active)));

        m_qbrContractLineLeftLineNumber.value(psaContractLineItems.ContractLineNum);
        m_qbrContractLineRightProjInvoiceProjId.value(psaContractLineItems.ProjInvoiceProjId);
        m_qbrContractLineRightLineNumberSelf.value(SysQuery::valueNot(psaContractLineItems.ContractLineNum));
        m_qbrContractLineRightLineNumber.value(Global::queryValue(psaContractLineItems.ContractLineNum));
    }

    element.activeControls();

    element.setVisibleTaxInformation_IN();
    element.setTaxWithholdEnabled_IN();

    if (enableMultipleContractLinesForProject)
    {
        element.filterLineTypeBasedOnBillingMethod(PSAContractLineItems_BillingMethod.selection());
    }
    return ret;
}

public void init()
{
    Query                   oQuery;
    QueryBuildDataSource    oqbdS;
    PSAContractLineItems    psaContractLineItemsLocal;
    PSAContractLineNum      billingRuleNum;
    Args                    args;

    super();

    args = element.args();
    if (args)
    {
        if (args.dataset() == tableNum(ProjInvoiceTable))
        {
            m_tProjInvoiceTable = args.record();
            m_ProjInvoiceProjId = m_tProjInvoiceTable.ProjInvoiceProjId;
        }

        // Drill through
        if (!args.record() && SysFormRun::isCalledFromJumpRef(args, fieldNum(PSAContractLineItems, ContractLineNum)))
        {
            billingRuleNum = args.lookupValue();
            if (billingRuleNum)
            {
                psaContractLineItemsLocal = PSAContractLineItems::find(billingRuleNum);

                // this variable is used to filter records to those of the contract.
                m_ProjInvoiceProjId = psaContractLineItemsLocal.ProjInvoiceProjId;
            }
        }

        // No filtering based on contract
        if (!m_ProjInvoiceProjId)
        {
            return;
        }

        oQuery = new Query();
        oqbdS = oQuery.addDataSource(tableNum(PSAContractLineItems), 'PSAConTractLineItems');
        oqbdS.addRange(fieldNum(PSAContractLineItems, ProjInvoiceProjId)).value(queryValue(m_ProjInvoiceProjId));

        this.query(oQuery);
    }
}

