void updateSalesPriceCache(ProjOnAccTransId _projTransId, SalesPrice _amount)
{
    if (salesPriceCache.exists(_projTransId))
    {
        salesPriceCache.remove(_projTransId);
    }

    salesPriceCache.insert(_projTransId, _amount);
}

