public void init()
{
    QueryBuildDataSource onAccTrans;
    QueryBuildRange      range;

    super();

    this.cacheAddMethod(tableMethodStr(ProjOnAccTrans, salesPrice));

    onAccTrans = projonacctrans_ds.queryBuildDataSource();
    range = onAccTrans.addRange(fieldNum(ProjOnAccTrans, psaContractLineNum));
    range.value(SysQuery::valueNotEmptyString());
}

edit SalesPrice editSalesPrice(boolean _set, ProjOnAccTrans _projOnAccTrans, SalesPrice _amount)
{
    if (_set)
    {
        this.updateSalesPriceCache(_projOnAccTrans.TransId, _amount);

        // When ONLY an unbound column is modified, leaveRecord method does not call write method.
        // since Sales Price is using an edit method(an unbound column), we need to call the DS write explicitly whenever value is modified
        projOnAccTrans_ds.forceWrite(NoYes::Yes);
    }
    else
    {
        if (!salesPriceCache.exists(_projOnAccTrans.TransId))
        {
            this.updateSalesPriceCache(_projOnAccTrans.TransId, _projOnAccTrans.salesPrice());
        }
    }

    return salesPriceCache.lookup(_projOnAccTrans.TransId);
}

public void initValue()
{
    ProjTable       projTable;
    super();

    projOnAccTrans.TransactionOrigin = ProjOrigin::Milestone;
    projOnAccTrans.psaContractLineNum = psaContractLineItems.ContractLineNum;

    select count(RecId) from projTable
        where projTable.ProjInvoiceProjId == psaContractLineItems.ProjInvoiceProjId
           && (element.enableMultipleContractLinesForProject || projTable.Type == ProjType::FixedPrice);

    if (projTable.RecId == 1)
    {
        select firstonly ProjId from projTable
            where projTable.ProjInvoiceProjId == psaContractLineItems.ProjInvoiceProjId
               && (element.enableMultipleContractLinesForProject || projTable.Type == ProjType::FixedPrice);

        projOnAccTrans.initFromProjTable(projTable.ProjId);
    }
}

public void write()
{
    // Don't save changes to transactions which are partially or fully invoiced
    if (ProjOnAccTrans.projInvoiceStatus() == ProjInvoiceStatus::FullyInvoiced ||
        ProjOnAccTrans.projInvoiceStatus() == ProjInvoiceStatus::PartiallyInvoiced)
    {
        return;
    }

    PSAContractLineItemsSetup::checkForOverlappingContractLines(projOnAccTrans.PSAContractLineNum, projOnAccTrans.ProjID);

    ttsbegin;

    super();

    if (projOnAccTrans.RecId)
    {
        ProjOnAccTransSale  updProjOnAccTransSale;

        delete_from updProjOnAccTransSale
            where updProjOnAccTransSale.TransId == projOnAccTrans.TransId;
    }

    // Provide a variable and method so that Chain of command can override the value stored in the cache
    SalesPrice onAccountSalesPrice = element.getOnAccountSalesPrice(projOnAccTrans.TransId);

    ProjOnAccTrans::createOnAccTrans(projOnAccTrans, onAccountSalesPrice);

    ttscommit;

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
        && ProjOnAccTrans.RecId
        && !TaxUpdateProjOnAccTransAV_INFlight::instance().isEnabled())
    {
        ProjOnAccTransTaxExtensionIN projOnAccTransTaxExtensionIN = ProjOnAccTrans.projOnAccTransTaxExtensionIN();
        ttsBegin;
        projOnAccTransTaxExtensionIN.selectForUpdate(true);
        ProjOnAccTransTaxExtensionIN.ProjOnAccTrans = ProjOnAccTrans.recId;
        ProjOnAccTransTaxExtensionIN.AssessableValueTransactionCurrency = onAccountSalesPrice * ProjOnAccTrans.Qty;
        projOnAccTransTaxExtensionIN.write();
        ttsCommit;
    }
    // </GIN>

    this.updateSalesPriceCache(projOnAccTrans.TransId, onAccountSalesPrice);
    projOnAccTrans_ds.reread();
    projOnAccTrans_ds.refresh();

    // Refresh contract value after milestone transaction is created/updated
    psacontractlineitems_ds.reread();
    psacontractlineitems_ds.refresh();
}

