/// <summary>
/// If <c>ProjEnableMultipleContractLinesForProjectFeature</c> is enabled:
/// If BillingMethod is "Fixed-price" then LineType can only be "Milestone" so set that value
/// </summary>
/// <returns>boolean</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if (enableMultipleContractLinesForProject)
    {
        ProjContractBillingMethod _billingMethod = this.selection();
        if (_billingMethod == ProjContractBillingMethod::FixedPrice)
        {
            PSAContractLineItems.LineType = PSAContractLineType::LumpSum;
        }
        else if (_billingMethod == ProjContractBillingMethod::TimeMaterial && psaContractLineItems_LineType.selection() == PSAContractLineType::LumpSum)
        {
            PSAContractLineItems.LineType = PSAContractLineType::Progress;
        }
        PSAContractLineItems_DS.refresh();
        PSAContractLineItems_LineType.modified();

        element.filterLineTypeBasedOnBillingMethod(_billingMethod);
    }

    return ret;
}

