public void lookup()
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(ProjTable), this);
    Query query = new Query();
    QueryBuildDataSource oqbdS = query.addDataSource(tableNum(ProjTable));
    QueryBuildRange oqbrContract = oqbdS.addRange(fieldNum(ProjTable, ProjInvoiceProjId));
    QueryBuildRange oqbrHeader = oqbdS.addRange(fieldNum(ProjTable, Header));

    oqbrContract.value(Global::queryValue(psaContractLineItems.ProjInvoiceProjId));
    oqbrHeader.value(Global::queryValue(NoYes::No));

    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjId));
    sysTableLookup.addLookupfield(fieldNum(ProjTable, Name));
    sysTableLookup.addLookupfield(fieldNum(ProjTable, CustAccount));
    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjInvoiceProjId));
    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjGroupId));

    if (!enableMultipleContractLinesForProject)
    {
        sysTableLookup.addLookupfield(fieldNum(ProjTable, Type));
    }

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

