        public boolean validate()
        {
            boolean ret;

            ProjTable    projTable;

            ret = super();

            if (this.text())
            {
                projTable = ProjTable::find(this.text());

                // when <c>ProjEnableMultipleContractLinesForProjectFeature</c> is turned on, Milestone is the only option for FixedPrice billing method
                if (projTable && projTable.Type != ProjType::FixedPrice && !element.enableMultipleContractLinesForProject)
                {
                    ret = ret && checkFailed(strFmt("@SYS62339", projTable.Type));

                    if (projTable.ProjInvoiceProjId != psaContractLineItems.ProjInvoiceProjId)
                    {
                        ret = ret && checkFailed(strFmt("@SYS133439", projTable.ProjId));
                    }
                }

                if(!ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled())
                {
                    // when <c>ProjEnableMultipleContractLinesForProjectFeature</c> is turned on, validate that there are no overlapping contract lines / transaction types
                    if (ret && element.enableMultipleContractLinesForProject)
                    {
                        str currentContractLineNum = PSAContractLineItems_ContractLineNum3.valueStr();

                        if (ProjMultipleContractLinesForProjectFeatureHelper::existOverlappingContractLineAssociationForProj(currentContractLineNum,
																															 PSAContractLineItems_ProjInvoiceProjId.valueStr(),
																															 projTable.ProjId,
																															 PSAContractLineItems_IncludeHourTrans.value(),
																															 PSAContractLineItems_IncludeExpenseTrans.value(),
																															 PSAContractLineItems_IncludeItemTrans.value(),
																															 PSAContractLineItems_IncludeFeeTrans.value()))
                        {
                            ret = checkFailed(strFmt("@Proj:OverlappingProjIdContractLineTransTypeUpdateError", currentContractLineNum, projTable.ProjInvoiceProjId, ProjTable.ProjId));
                        }
                    }
                }
            }

            return ret;
        }

