[Form]
    public class PSAContractStatus extends FormRun
    {
        QueryBuildRange                     m_oRangeContract;
        ProjInvoiceTable                    m_tProjInvoiceTable;

        [FormObservable]
        ProjInvoiceProjId                   m_sProjInvoiceProjId;
        [FormObservable]
        Amount                              m_rTotalLineValues, m_rTotalInvoicedAmounts;
        [FormObservable]
        Amount                              m_rLineInvoicedAmount;
        [FormObservable]
        Qty                                 m_rLineInvoicedUnit;
        Map                                 invoiceShowCache;
        Map                                 totalInvoicedCache; 

}

public void clearCache()
{
    invoiceShowCache = new Map(Types::Int64, Types::Real);
    totalInvoicedCache = new Map(Types::String, Types::Real);
}

public void init()
{
    PSAContractLineItems    psaContractLineItemsLocal;

    super();

    switch (element.args().dataset())
    {
        case tableNum(ProjInvoiceTable) :
            m_tProjInvoiceTable = element.args().record();
            m_sProjInvoiceProjId = m_tProjInvoiceTable.ProjInvoiceProjId;
            break;

        case tableNum(PSAContractLineItems) :
            psaContractLineItemsLocal = element.args().record();
            m_sProjInvoiceProjId = psaContractLineItemsLocal.ProjInvoiceProjId;
            m_tProjInvoiceTable = ProjInvoiceTable::find(m_sProjInvoiceProjId);
            break;
    }

    if (m_sProjInvoiceProjId != '')
    {
        contractEdit.text(m_sProjInvoiceProjId);
    }
    this.clearCache();
    element.showHideFields();

    // If multiple contract lines per project feature is enabled, change column label from "Billing rule" to "Contract line".
    boolean multipleContractLinesFeatureEnabled = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled();
    if (multipleContractLinesFeatureEnabled)
    {
        ReviewContract.text("@Proj:ReviewContractLine");
    }
}

[SysClientCacheDataMethodAttribute(true)]
display Amount totalInvoiced()
{
    if (!totalInvoicedCache.exists(m_sProjInvoiceProjId))
    {
        m_rTotalInvoicedAmounts = PSAContractLineItemsServer::totalContractLineInvoicedAmounts(m_sProjInvoiceProjId, includeFutureInvoices.value());
        totalInvoicedCache.insert(m_sProjInvoiceProjId, m_rTotalInvoicedAmounts);
    }
    return totalInvoicedCache.lookup(m_sProjInvoiceProjId);
}

[SysClientCacheDataMethodAttribute(true)]
display Amount totalPaid()
{
    Amount m_rTotalPaid;

    m_rTotalPaid = PSAContractLineItemsServer::totalContractLinePaid(m_sProjInvoiceProjId);

    return m_rTotalPaid;
}

[SysClientCacheDataMethodAttribute(true)]
display PSAPercent totalPercentComplete()
{
    PSAPercent  rPercent;

    if (m_rTotalLineValues == 0)
    {
        rPercent = 0;
    }
    else
    {
        rPercent = 100 * m_rTotalInvoicedAmounts / m_rTotalLineValues;
    }

    return rPercent;
}

[SysClientCacheDataMethodAttribute(true)]
display Amount totalRemaining()
{
    return m_rTotalLineValues - m_rTotalInvoicedAmounts;
}

[SysClientCacheDataMethodAttribute(true)]
display Amount totalValue()
{
    m_rTotalLineValues = PSAContractLineItemsServer::totalContractLineValues(m_sProjInvoiceProjId);

    return m_rTotalLineValues;
}

private void showHideMenuItems()
{
    PSAUnitsUpdateLog       tpsaUnitsUpdateLog;
    PSAPercentUpdateLog     tpsaPercentUpdateLog;
    boolean                 allowMilestoneStatusUpdate;

    psaUpdatePercentMI.enabled(false);
    psaUpdateUnitsMI.enabled(false);

    if ((psaContractLineItems.LineType == PSAContractLineType::Progress)
           && (psaContractLineItems.IsCrossReference == NoYes::No))
    {
        tpsaPercentUpdateLog = PSAPercentUpdateLog::find(psaContractLineItems.ContractLineNum);
        // The percentage can only be update if there's no progress update yet to be posted.
        if (!tpsaPercentUpdateLog || tpsaPercentUpdateLog.IsProposal == NoYes::No || tpsaPercentUpdateLog.IsInvoiced == NoYes::Yes)
        {
            psaUpdatePercentMI.enabled(true);
        }
    }

    if (psaContractLineItems.LineType == PSAContractLineType::UOD)
    {
        tpsaUnitsUpdateLog = PSAUnitsUpdateLog::find(psaContractLineItems.ContractLineNum);
        if (tpsaUnitsUpdateLog)
        {
            if (tpsaUnitsUpdateLog.IsProposal == NoYes::Yes)
            {
                if (tpsaUnitsUpdateLog.IsInvoiced == NoYes::Yes)
                {
                    psaUpdateUnitsMI.enabled(true);
                }
            }
            else
            {
                psaUpdateUnitsMI.enabled(true);
            }
        }
        else
        {
            psaUpdateUnitsMI.enabled(true);
        }
    }

    allowMilestoneStatusUpdate = (psacontractlineItems.LineType == PSAContractLineType::LumpSum)? true: false;
    UpdateMilestoneStatus.enabled(allowMilestoneStatusUpdate);
}

private void showHideFields()
{
    psaContractLineItems psaContractLineItemsLocal;

    select firstonly RecId from psaContractLineItemsLocal
        where psaContractLineItemsLocal.ProjInvoiceProjId == m_sProjInvoiceProjId &&
              psaContractLineItemsLocal.LineType == PSAContractLineType::UOD;
    if (psaContractLineItemsLocal)
    {
        UnitsTab.visible(true);
    }
}

public int active()
{
    int ret;

    ret = super();

    element.showHideMenuItems();

    return ret;
}

[SysClientCacheDataMethodAttribute(true)]
display Amount retainedShow(PSAContractLineItems _contractLine)
{
    Amount rRetainedAmount;

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        rRetainedAmount = PSAContractInvoiceRetain::calcRetainedAmount(_contractLine.ContractLineNum, '', includeFutureInvoices.value());
    }
    else
    {
        rRetainedAmount = 0;
    }

    return rRetainedAmount;
}

[SysClientCacheDataMethodAttribute(true)]
display Amount remainingShow(PSAContractLineItems _contractLine)
{
    Amount rRemainValue;

    rRemainValue = _contractLine.LineValue - this.invoicedShow(_contractLine);
    if (rRemainValue < 0)
    {
        rRemainValue = 0;
    }

    return rRemainValue;
}

[SysClientCacheDataMethodAttribute(true)]
display PSAPercent percentCompleteShow(PSAContractLineItems _contractLine)
{
    PSAPercent  rPercent;

    if (_contractLine.LineValue == 0)
    {
        rPercent = 0;
    }
    else
    {
        rPercent = 100 * this.invoicedShow(_contractLine) / _contractLine.LineValue;
    }

    return rPercent;
}

[SysClientCacheDataMethodAttribute(true)]
display Amount invoicedShow(PSAContractLineItems _contractLine)
{
    if (!invoiceShowCache.exists(_contractLine.RecId))
    {
        m_rLineInvoicedAmount = PSAContractLineItemsServer::getContractLineInvoicedAmounts(_contractLine.ContractLineNum, '', includeFutureInvoices.value());
        invoiceShowCache.insert(_contractLine.RecId, m_rLineInvoicedAmount);
    }

    return invoiceShowCache.lookup(_contractLine.RecId);
}

public void init()
{
    Query                   oQuery;
    QueryBuildDataSource    oContractDS;

    super();

    oQuery = new Query();
    oContractDS = oQuery.addDataSource(tableNum(PSAContractLineItems));
    m_oRangeContract = oContractDS.addRange(fieldNum(PSAContractLineItems, ProjInvoiceProjId));

    this.query(oQuery);

    // If multiple contract lines per project feature is enabled
    // change column label from "Billing rule" to "Contract line"
    boolean multipleContractLinesFeatureEnabled = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled();
    if (multipleContractLinesFeatureEnabled)
    {
        PSAContractLineItems_ContractLineNum.label("@PSA2823");
    }

}

public void executeQuery()
{
    m_oRangeContract.value(contractEdit.text());

    super();
}

[SysClientCacheDataMethodAttribute(true)]
display Qty unitsRemainingShow(PSAContractLineItems _contractLine)
{
    Qty rRemainUnit;

    rRemainUnit = _contractLine.Units - PSAContractLineItemsServer::getContractLineInvoicedUnitsForUOD(_contractLine.ContractLineNum);
    if (rRemainUnit < 0)
    {
        rRemainUnit = 0;
    }

    return rRemainUnit;
}

[SysClientCacheDataMethodAttribute(true)]
display PSAPercent unitsPercentCompleteShow(PSAContractLineItems _contractLine)
{
    PSAPercent  rPercent;

    if (_contractLine.Units == 0)
    {
        rPercent = 0;
    }
    else
    {
        rPercent = 100 * PSAContractLineItemsServer::getContractLineInvoicedUnitsForUOD(_contractLine.ContractLineNum) / _contractLine.Units;
    }

    return rPercent;
}

[SysClientCacheDataMethodAttribute(true)]
display Qty unitsRetainedShow(PSAContractLineItems _contractLine)
{
    Qty rRetainedUnit;

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        if (_contractLine.UnitPrice != 0)
        {
            rRetainedUnit = PSAContractInvoiceRetain::calcRetainedAmount(_contractLine.ContractLineNum, '', includeFutureInvoices.value()) / _contractLine.UnitPrice;
        }
    }
    else
    {
        rRetainedUnit = 0;
    }

    return rRetainedUnit;
}

[SysClientCacheDataMethodAttribute(true)]
display Qty unitsInvoicedShow(PSAContractLineItems _contractLine)
{
    m_rLineInvoicedUnit = PSAContractLineItemsServer::getContractLineInvoicedUnitsForUOD(_contractLine.ContractLineNum);

    return m_rLineInvoicedUnit;
}

void clicked()
{
    Args                    args;

    if (!m_tProjInvoiceTable)
    {
        throw error("@PSA2873");
    }
    else
    {
        args = new Args();
        args.record(m_tProjInvoiceTable);
        args.caller(element);
        new MenuFunction(menuItemDisplayStr(ProjInvoiceJournal), MenuItemType::Display).run(args);
    }

    super();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    m_tProjInvoiceTable = ProjInvoiceTable::find(contractEdit.text());
    m_sProjInvoiceProjId = contractEdit.text();

    psaContractLineItems_DS.executeQuery();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    psaContractLineItems_DS.executeQuery();
    element.clearCache();

    return ret;
}
