[Form]
public class PSAProjInvoiceDetails extends FormRun
{
    QueryBuildRange             m_oqbrLineNum, m_oqbrProposalId, m_oqbrProposalIdSel;

}

void addSelected(PSATmpProjProposalTrans _tProposalTrans)
{
    PSAProjInvoiceDetailTrans tDetailTrans;

    tDetailTrans.ProposalId = projProposalJour.ProposalId;
    tDetailTrans.LineNum = psaProjInvoiceDetailLine.LineNum;
    tDetailTrans.RefTableId = _tProposalTrans.RefTableId;
    tDetailTrans.RefRecId = _tProposalTrans.RefRecIdTrans;
    tDetailTrans.TransId = _tProposalTrans.RefTransId;
    tDetailTrans.RefLineRecId = psaProjInvoiceDetailLine.RecId;
    tDetailTrans.insert();

    psaProjInvoiceDetailLine.LineAmount += _tProposalTrans.LineAmount;
    psaProjInvoiceDetailLine.write();
    psaProjInvoiceDetailLine_ds.refresh();
}

public void close()
{
    FormRun        formRun;
    ListEnumerator rootDsEnumerator;
    FormDataSource dataSource;

    super();

    // Get an instance of the calling form.
    formRun = element.args().caller();

    // If the caller is a form, refresh that form.
    if (formRun)
    {
        rootDsEnumerator = formRun.rootFormDataSources().getEnumerator();
        while (rootDsEnumerator.moveNext())
        {
            dataSource = rootDsEnumerator.current();
            if (dataSource.table() == tableNum(ProjProposalJour))
            {
                dataSource.reread();
                dataSource.refresh();
            }
        }
    }
}

public void init()
{
    super();


    // If multiple contract lines per project feature is enabled, change column label from "Billing rule" to "Contract line".
    boolean multipleContractLinesFeatureEnabled = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled();
    if (multipleContractLinesFeatureEnabled)
    {
        ContractLineType.label("@SYS9844");
        ContractLineNum.label("@Proj:ContractLine");
        ContractTxt.label("@Proj:ContractDescription");
        PSATmpProjProposalTransContractLineType.label("@SYS9844");
        PSATmpProjProposalTransContractLineNum.label("@Proj:ContractLine");
        PSATmpProjProposalTransTxt.label("@Proj:ContractDescription");
    }

}

void moveDown()
{
    PSAProjInvoiceDetailLine        tCur;

    tCur = psaProjInvoiceDetailLine.moveDown();
    if (tCur)
    {
        psaProjInvoiceDetailLine_ds.research();
        psaProjInvoiceDetailLine_ds.positionToRecord(tCur);
    }
}

void moveUp()
{
    PSAProjInvoiceDetailLine        tSave;

    tSave = psaProjInvoiceDetailLine.moveUp();

    if (tSave)
    {
        psaProjInvoiceDetailLine_ds.research();
        psaProjInvoiceDetailLine_ds.positionToRecord(tSave);
        psaProjInvoiceDetailTrans_ds.research();
        psaProjInvoiceDetailTrans_ds.positionToRecord(tSave);
    }
}

void removeSelected(PSATmpProjProposalTrans _tProposalTrans)
{
    PSAProjInvoiceDetailTrans tDetailTrans;

    delete_from tDetailTrans
        where tDetailTrans.ProposalId == projProposalJour.ProposalId
        &&    tDetailTrans.LineNum == psaProjInvoiceDetailLine.LineNum
        &&    tDetailTrans.RefTableId == _tProposalTrans.RefTableId
        &&    tDetailTrans.RefRecId == _tProposalTrans.RefRecIdTrans;

    psaProjInvoiceDetailLine.LineAmount -= _tProposalTrans.LineAmount;
    psaProjInvoiceDetailLine.write();
    psaProjInvoiceDetailLine_ds.refresh();
}

public void run()
{
    super();

    psaTmpProjProposalTrans.setTmpData(PSAProjInvoiceDetails::proposalTrans(projProposalJour.ProposalId, psaTmpProjProposalTrans));
    psaTmpProjProposalTrans_ds.executeQuery();

    psaTmpProjProposalTransSelected.setTmpData(PSAProjInvoiceDetails::proposalTrans(projProposalJour.ProposalId, psaTmpProjProposalTransSelected));
    psaTmpProjProposalTransSelected_ds.executeQuery();

    if (PSAContractLineItems::hasBillingRules(projProposalJour.ProjInvoiceProjId))
    {
        selectedTransactions.visible(false);
        availableTransactions.visible(false);
    }
    else
    {
        selectedBillingTransactions.visible(false);
        availableBillingTransactions.visible(false);
    }
}

public void delete()
{
    super();

    psaProjInvoiceDetailTransSelected_ds.executeQuery();
    psaTmpProjProposalTransSelected_ds.executeQuery();
    psaProjInvoiceDetailTrans_ds.executeQuery();
    psaTmpProjProposalTrans_ds.executeQuery();
    projProposalJour_ds.executeQuery();
}

public int active()
{
    int ret;

    ret = super();

    psaProjInvoiceDetailTransSelected_ds.executeQuery();
    psaTmpProjProposalTransSelected_ds.executeQuery();
    psaProjInvoiceDetailTrans_ds.executeQuery();
    psaTmpProjProposalTrans_ds.executeQuery();
    return ret;
}

public void modified()
{
    super();
    AvailableTransactions.pageActivated();
}

public int active()
{
    int ret;

    ret = super();

    PSAbtPrintPreview.enabled(true);

    if (ProjProposalJour.LineProperty == ProjLinePropertyCode::Invoiced ||
        ProjProposalJour.LineProperty == ProjLinePropertyCode::Canceled)
    {
        PSAbtPrintPreview.enabled(false);
    }

    return ret;
}

public void modified()
{
    super();
    projProposalJour.write();
}

public void executeQuery()
{
    PSAProjInvoiceDetailTrans tDetailTrans;

    if (psaTmpProjProposalTrans_qr && !this.queryRunQueryBuildDataSource().sortFieldCount())
    {
        if (!psaTmpProjProposalTrans_qr.query().dataSourceTable(tableNum(PSATmpProjProposalTrans)).sortFieldCount())
        {
            select * from psaTmpProjProposalTrans
                where psaTmpProjProposalTrans.TransType       != ProjTransType::IndirectComponent
                    notexists join tDetailTrans
                where psaTmpProjProposalTrans.RefTableId      == tDetailTrans.RefTableId &&
                    psaTmpProjProposalTrans.RefRecIdTrans     == tDetailTrans.RefRecId &&
                    projProposalJour.ProposalId               == tDetailTrans.ProposalId;
        }
    }

    super();

    psaTmpProjProposalTrans_ds.refresh();
    psaTmpProjProposalTrans_ds.reread();
}

public void executeQuery()
{
    PSAProjInvoiceDetailTrans tDetailTrans;

    super();

    if (PSATmpProjProposalTransSelected_qr && !this.queryRunQueryBuildDataSource().sortFieldCount())
    {
        if (!PSATmpProjProposalTransSelected_qr.query().dataSourceTable(tablenum(PSATmpProjProposalTrans)).sortFieldCount())
        {
            select * from PSATmpProjProposalTransSelected
                where PSATmpProjProposalTransSelected.TransType       != ProjTransType::IndirectComponent
                    exists join tDetailTrans
                where PSATmpProjProposalTransSelected.RefTableId      == tDetailTrans.RefTableId &&
                    PSATmpProjProposalTransSelected.RefRecIdTrans     == tDetailTrans.RefRecId &&
                    PSAProjInvoiceDetailLine.RecId                    == tDetailTrans.RefLineRecId;
        }
    }

    PSATmpProjProposalTransSelected_ds.refresh();
    PSATmpProjProposalTransSelected_ds.reread();
}

public void init()
{
    super();
    this.query().dataSourceName('PSATmpProjProposalTransSelected').clearDynalinks();
}

public void executeQuery()
{
    m_oqbrLineNum.value(queryValue(psaProjInvoiceDetailLine.LineNum));
    m_oqbrProposalIdSel.value(projProposalJour.ProposalId);
    super();
}

public void init()
{
    super();

    m_oqbrLineNum = this.query().dataSourceName('PSAProjInvoiceDetailTransSelected').addRange(fieldNum(PSAProjInvoiceDetailTrans, LineNum));
    m_oqbrProposalIdSel = this.query().dataSourceName('PSAProjInvoiceDetailTransSelected').addRange(fieldNum(PSAProjInvoiceDetailTrans, ProposalId));

    this.query().dataSourceName('PSAProjInvoiceDetailTransSelected').addLink(fieldNum(PSATmpProjProposalTrans, RefTableId), fieldNum(PSAProjInvoiceDetailTrans, RefTableId));
    this.query().dataSourceName('PSAProjInvoiceDetailTransSelected').addLink(fieldNum(PSATmpProjProposalTrans, RefRecIdTrans), fieldNum(PSAProjInvoiceDetailTrans, RefRecId));
}

public void executeQuery()
{
    m_oqbrProposalId.value(projProposalJour.ProposalId);
    super();
}

public void init()
{
    super();

    m_oqbrProposalId = this.query().dataSourceName('PSAProjInvoiceDetailTrans').addRange(fieldNum(PSAProjInvoiceDetailTrans, ProposalId));

    this.query().dataSourceName('PSAProjInvoiceDetailTrans').addLink(fieldNum(PSATmpProjProposalTrans, RefTableId), fieldNum(PSAProjInvoiceDetailTrans, RefTableId));
    this.query().dataSourceName('PSAProjInvoiceDetailTrans').addLink(fieldNum(PSATmpProjProposalTrans, RefRecIdTrans), fieldNum(PSAProjInvoiceDetailTrans, RefRecId));
}

