void clicked()
{
    if (PSAProjInvoiceDetailLine::isManage(projProposalJour.ProposalId) &&
        projProposalJour.psaInvoiceFormats != PSAInvoiceFormats::ManageInvoice)
    {
        ttsbegin;
        // ensure that we have the current record
        projProposalJour_ds.reread();
        projProposalJour.psaInvoiceFormats = PSAInvoiceFormats::ManageInvoice;
        projProposalJour.update();
        ttscommit;
    }
    ProjFormLetter::printPreview(projProposalJour);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        AvailableTransactions.pageActivated();
    }

    return ret;
}

void clicked()
{
    PSATmpProjProposalTrans tProposalTrans;

    tProposalTrans = psaTmpProjProposalTransSelected_ds.getFirst(1);

    if (!tProposalTrans)
    {
        element.removeSelected(psaTmpProjProposalTransSelected);
    }
    else
    {
        while (tProposalTrans)
        {
            element.removeSelected(tProposalTrans);
            tProposalTrans = psaTmpProjProposalTransSelected_ds.getNext();
        }
    }

    psaTmpProjProposalTransSelected_ds.executeQuery();
    psaTmpProjProposalTrans_ds.executeQuery();
    psaProjInvoiceDetailLine_ds.reread();
}

