void clicked()
{
    PSATmpProjProposalTrans tProposalTrans;

    if (!psaProjInvoiceDetailLine.ProposalId)
    {
        return;
    }

    psaProjInvoiceDetailLine.write();

    tProposalTrans = psaTmpProjProposalTrans_ds.getFirst(1);

    if (!tProposalTrans)
    {
        element.addSelected(psaTmpProjProposalTrans);
    }
    else
    {
        while (tProposalTrans)
        {
            element.addSelected(tProposalTrans);
            tProposalTrans = psaTmpProjProposalTrans_ds.getNext();
        }
    }

    psaTmpProjProposalTransSelected_ds.executeQuery();
    psaTmpProjProposalTrans_ds.executeQuery();
    psaProjInvoiceDetailLine_ds.reread();
}

