public class PSAPwpInvoiceInquiry extends FormRun
{
    FromDate                fromDate;
    ToDate                  toDate;

    VendAccount             vendorAccount;
    InvoiceId               vendorInvoiceId;
    ProjId                  projId;
    PurchId                 purchId;
    ProjInvoiceProjId       projContract;
    NoYes                   isPwp;
    PSAVendInvoiceStatus    invoiceStatus;

    Query                   queryPwp;

    QueryBuildRange         qbrPwp;
    QueryBuildRange         qbrVendInvoiceStatusPurch;
    QueryBuildRange         qbrVendInvoiceStatusJournal;
    QueryBuildRange         qbrProjPurch;
    QueryBuildRange         qbrProjJournal;
    QueryBuildRange         qbrVendAccountPurch;
    QueryBuildRange         qbrVendAccountJournal;
    QueryBuildRange         qbrContractPurch;
    QueryBuildRange         qbrContractJournal;
    QueryBuildRange         qbrTransDatePurch;
    QueryBuildRange         qbrTransDateJournal;
    QueryBuildRange         qbrPurchId;
    QueryBuildRange         qbrInvoicePurch;
    QueryBuildRange         qbrInvoiceJournal;
    QueryBuildRange         qbrJournalTransVoucher;
    QueryBuildRange         qbrVendTransTypePurch;
    QueryBuildRange         qbrVendTransTypeJournal;
    QueryBuildRange         qbrPayWhenPaid;
    QueryBuildRange         qbrJournalTransDate;

    boolean                 isCalledFromGeneralInquery;
    Notes                   projInvoiceIds;

