void clearProjInvoiceJourDS()
{
    select projInvoiceJour where projInvoiceJour.RecId == 0;
}

void enforcePWP()
{
    VendInvoiceTrans        vendInvoiceTransLocal;

    for (vendInvoiceTransLocal = vendInvoiceTrans_ds.getFirst(true) ? vendInvoiceTrans_ds.getFirst(true) : vendInvoiceTrans_ds.cursor();
         vendInvoiceTransLocal; vendInvoiceTransLocal = vendInvoiceTrans_ds.getNext())
    {
        if ((vendInvoiceTransLocal.purchLine().IsPwp == NoYes::Yes) && (vendInvoiceTransLocal.IsPwp == NoYes::No) && (vendInvoiceTransLocal.ReadyForPayment == NoYesCombo::Yes))
        {
            ttsbegin;
            vendInvoiceTransLocal.IsPwp             = NoYes::Yes;
            vendInvoiceTransLocal.ReadyForPayment   = NoYesCombo::No;
            vendInvoiceTransLocal.update();
            ttscommit;
        }
    }
    vendInvoiceTrans_ds.research(true);
}

void fetchCustomerInvoiceDetails()
{
    ProjInvoiceJour                 projInvoiceJourLocal;

    void addInvoiceToList(ProjInvoiceProjId _invoiceId)
    {
        if (_invoiceId != '')
        {
            if (projInvoiceIds != '')
            {
                projInvoiceIds += ", ";
            }
            projInvoiceIds += _invoiceId;
        }
    } 

    if (vendTrans.Invoice)
    {
        projInvoiceIds = "";       

        if (vendTrans.isTypePurch())
        {
            boolean invoiceFound = false;

            ProjItemTrans projItemTrans;
            ProjInvoiceItem projInvoiceItem;
            while select ProjInvoiceId from projInvoiceJourLocal
                join RecId from projInvoiceItem
                    where projInvoiceItem.ProjInvoiceId == projInvoiceJourLocal.ProjInvoiceId &&
                        projInvoiceItem.InvoiceDate == projInvoiceJourLocal.InvoiceDate
                join RecId from projItemTrans
                    where projItemTrans.ProjTransId == projInvoiceItem.ProjTransId &&
                            projItemTrans.VendInvoiceTransRecId == vendInvoiceTrans.RecId
            {
                addInvoiceToList(projInvoiceJourLocal.ProjInvoiceId);
                invoiceFound = true;
            }

            if (!invoiceFound)
            {
                ProjCostTrans projCostTrans;
                ProjInvoiceCost projInvoiceCost;
                while select ProjInvoiceId from projInvoiceJourLocal
                    join RecId from projInvoiceCost
                        where projInvoiceCost.ProjInvoiceId == projInvoiceJourLocal.ProjInvoiceId &&
                            projInvoiceCost.InvoiceDate == projInvoiceJourLocal.InvoiceDate
                    join RecId from projCostTrans
                        where projCostTrans.TransId == projInvoiceCost.TransId &&
                                projCostTrans.VendInvoiceTrans == vendInvoiceTrans.RecId
                {
                    addInvoiceToList(projInvoiceJourLocal.ProjInvoiceId);
                    invoiceFound = true;
                }
            }

            if (!invoiceFound)
            {
                ProjEmplTrans projEmplTrans;
                ProjInvoiceEmpl projInvoiceEmpl;
                while select ProjInvoiceId from projInvoiceJourLocal
                    join RecId from projInvoiceEmpl
                        where projInvoiceEmpl.ProjInvoiceId == projInvoiceJourLocal.ProjInvoiceId &&
                            projInvoiceEmpl.InvoiceDate == projInvoiceJourLocal.InvoiceDate
                    join RecId from projEmplTrans
                        where projEmplTrans.TransId == projInvoiceEmpl.TransId &&
                                projEmplTrans.VendInvoiceTrans == vendInvoiceTrans.RecId
                {
                    addInvoiceToList(projInvoiceJourLocal.ProjInvoiceId);
                    invoiceFound = true;
                }
            }
        }
        else
        {
            ProjCostTrans projCostTrans;
            ProjInvoiceCost projInvoiceCost;
            while select firstOnly ProjInvoiceId, proposalId from projInvoiceJourLocal
                join RecId from projInvoiceCost
                    where projInvoiceCost.ProjInvoiceId == projInvoiceJourLocal.ProjInvoiceId &&
                        projInvoiceCost.InvoiceDate == projInvoiceJourLocal.InvoiceDate
                join RecId from projCostTrans
                    where projCostTrans.TransId == projInvoiceCost.TransId &&
                        projCostTrans.LedgerJournalTrans == LedgerJournalTrans_Project.RefRecId
            {
                addInvoiceToList(projInvoiceJourLocal.ProjInvoiceId);
            }
        }

        projInvoiceJour_ds.executeQuery();
    }
}

public void init()
{
    ProjTable           projTable;
    VendTable           vendTable;
    ProjInvoiceTable    projInvoiceTable;
    VendTrans           vendTransLocal;

    super();

    if (element.args().caller())
    {
        switch (element.args().dataset())
        {
            case tableNum(ProjTable):
                projTable = element.args().record();
                projId = projTable.ProjId;
                projContract = projTable.ProjInvoiceProjId;
                project.text(projTable.ProjId);
                qbrProjJournal.value(projTable.ProjId);
                qbrProjPurch.value(projTable.ProjId);
                projectContract.text(projTable.ProjInvoiceProjId);
                break;

            case tableNum(VendTable):
                vendTable = element.args().record();
                vendorAccount = vendTable.AccountNum;
                vendAccount.text(vendTable.AccountNum);
                qbrVendAccountPurch.value(vendTable.AccountNum);
                qbrVendAccountJournal.value(vendTable.AccountNum);
                vendAccount.text(vendTable.AccountNum);
                break;

            case tableNum(ProjInvoiceTable):
                projInvoiceTable = element.args().record();
                projContract = projInvoiceTable.ProjInvoiceProjId;
                projectContract.text(projInvoiceTable.ProjInvoiceProjId);
                qbrContractPurch.value(projInvoiceTable.ProjInvoiceProjId);
                qbrContractJournal.value(projInvoiceTable.ProjInvoiceProjId);
                break;

            case tableNum(VendTrans):
                vendTransLocal = element.args().record();
                if (vendTransLocal.Invoice)
                {
                    vendAccount.text(vendTransLocal.AccountNum);
                    vendInvoiceId.text(vendTransLocal.Invoice);
                    qbrInvoicePurch.value(vendTransLocal.Invoice);
                    qbrInvoiceJournal.value(vendTransLocal.Invoice);
                }
                else
                {
                    vendAccount.text(vendTransLocal.AccountNum);
                    qbrVendAccountPurch.value(vendTransLocal.AccountNum);
                    qbrVendAccountJournal.value(vendTransLocal.AccountNum);
                }
                break;
        }
    }
    else
    {
        isCalledFromGeneralInquery = true;
    }
}

void releasePWPForJournalLine(boolean _set)
{
    ttsBegin;
    LedgerJournalTrans_Project.ReadyForPayment = _set ? NoYesCombo::Yes : NoYesCombo::No;
    LedgerJournalTrans_Project.update();
    ttsCommit;

    LedgerJournalTrans_Project_DS.updateDesign();
    LedgerJournalTrans_Project_DS.refresh();
}

void releasePWP()
{
    VendInvoiceTrans        vendInvoiceTransLocal;

    for (vendInvoiceTransLocal = vendInvoiceTrans_ds.getFirst(true) ? vendInvoiceTrans_ds.getFirst(true) : vendInvoiceTrans_ds.cursor();
         vendInvoiceTransLocal; vendInvoiceTransLocal = vendInvoiceTrans_ds.getNext())
    {
        if ((vendInvoiceTransLocal.IsPwp == NoYes::Yes) && (vendInvoiceTransLocal.ReadyForPayment == NoYesCombo::No))
        {
            ttsbegin;
            vendInvoiceTransLocal.IsPwp             = NoYes::No;
            vendInvoiceTransLocal.ReadyForPayment   = NoYesCombo::Yes;
            vendInvoiceTransLocal.update();
            ttscommit;
        }
    }
    vendInvoiceTrans_ds.research(true);
}

public void searchRecords()
{
    isCalledFromGeneralInquery = false;

    if (fromDate && toDate)
    {
        qbrTransDatePurch.value(SysQuery::range(fromDate, toDate));
        qbrTransDateJournal.value(SysQuery::range(fromDate, toDate));
    }

    if (vendorAccount)
    {
        qbrVendAccountPurch.value(vendorAccount);
        qbrVendAccountJournal.value(vendorAccount);
    }

    if (vendorInvoiceId)
    {
        qbrInvoicePurch.value(vendorInvoiceId);
        qbrInvoiceJournal.value(vendorInvoiceId);
    }

    if (projId)
    {
        qbrProjPurch.value(projId);
        qbrProjJournal.value(projId);
    }

    if (purchId)
    {
        qbrPurchId.value(purchId);
        qbrVendTransTypePurch.value(SysQuery::value(LedgerTransType::Purch));
        qbrVendTransTypeJournal.value(SysQuery::value(LedgerTransType::Purch));
    }
    else
    {
        qbrVendTransTypePurch.value(SysQuery::valueUnlimited());
        qbrVendTransTypeJournal.value(SysQuery::valueUnlimited());
    }

    if (projContract)
    {
        qbrContractPurch.value(projContract);
        qbrContractJournal.value(projContract);
    }

    if (isPwp)
    {
        qbrPwp.value(queryValue(isPwp));
    }

    if (invoiceStatus == PSAVendInvoiceStatus::All)
    {
        qbrVendInvoiceStatusPurch.value("");
        qbrVendInvoiceStatusJournal.value("");
    }
    else if (invoiceStatus == PSAVendInvoiceStatus::Open)
    {
        qbrVendInvoiceStatusPurch.value(SysQuery::valueEmptyString());
        qbrVendInvoiceStatusJournal.value(SysQuery::valueEmptyString());
    }
    else
    {
        qbrVendInvoiceStatusPurch.value(SysQuery::valueNotEmptyString());
        qbrVendInvoiceStatusJournal.value(SysQuery::valueNotEmptyString());
    }

    vendTrans_ds.executeQuery();
}

void setButtonAccess()
{
    if (vendInvoiceTrans.ReadyForPayment == NoYesCombo::Yes)
    {
        releasePWP.enabled(false);
    }
    else
    {
        releasePWP.enabled(true);
    }

    if ((vendInvoiceTrans.purchLine().IsPwp == NoYes::Yes) && (vendInvoiceTrans.IsPwp == NoYes::No))
    {
        enforcePWP.enabled(true);
    }
    else
    {
        enforcePWP.enabled(false);
    }
}

public int active()
{
    int ret;

    element.clearProjInvoiceJourDS();

    ret = super();

    if (vendTrans.isTypePurch())
    {
        // MiddleTab contains the invoice lines
        element.control(element.controlId(formControlStr(PSAPwpInvoiceInquiry, MiddleTab))).visible(true);
        element.control(element.controlId(formControlStr(PSAPwpInvoiceInquiry, VendInvoiceJournalTab))).visible(false);
    }
    else
    {
        element.control(element.controlId(formControlStr(PSAPwpInvoiceInquiry, MiddleTab))).visible(false);
        element.control(element.controlId(formControlStr(PSAPwpInvoiceInquiry, VendInvoiceJournalTab))).visible(true);
    }

    return ret;
}

display VendName vendName(VendTrans _vendTrans)
{
    return VendTable::find(_vendTrans.AccountNum).name();
}

public void executeQuery()
{
    if (!isCalledFromGeneralInquery)
    {
        element.clearProjInvoiceJourDS();

        super();
    }
}

public void init()
{
    super();

    if (ProjPwpInvoiceInquiryV2Flight::instance().isEnabled())
    {
        queryPwp = new Query(queryStr(PSAPwpInvoiceInquiryV2));
    }
    else
    {
        queryPwp = new Query(queryStr(PSAPwpInvoiceInquiry));
    }

    qbrVendInvoiceStatusPurch   = queryPwp.dataSourceTable(tableNum(VendTrans), 1).findRange(fieldNum(VendTrans, Closed));
    qbrVendInvoiceStatusJournal = queryPwp.dataSourceTable(tableNum(VendTrans), 2).findRange(fieldNum(VendTrans, Closed));

    qbrVendAccountPurch         = queryPwp.dataSourceTable(tableNum(VendTrans), 1).findRange(fieldNum(VendTrans, AccountNum));
    qbrVendAccountJournal       = queryPwp.dataSourceTable(tableNum(VendTrans), 2).findRange(fieldNum(VendTrans, AccountNum));

    qbrInvoicePurch             = queryPwp.dataSourceTable(tableNum(VendTrans), 1).findRange(fieldNum(VendTrans, Invoice));
    qbrInvoiceJournal           = queryPwp.dataSourceTable(tableNum(VendTrans), 2).findRange(fieldNum(VendTrans, Invoice));

    qbrTransDatePurch           = queryPwp.dataSourceTable(tableNum(VendTrans), 1).findRange(fieldNum(VendTrans, TransDate));
    qbrTransDateJournal         = queryPwp.dataSourceTable(tableNum(VendTrans), 2).findRange(fieldNum(VendTrans, TransDate));

    qbrVendTransTypePurch       = queryPwp.dataSourceTable(tableNum(VendTrans), 1).findRange(fieldNum(VendTrans, TransType));
    qbrVendTransTypeJournal     = queryPwp.dataSourceTable(tableNum(VendTrans), 2).findRange(fieldNum(VendTrans, TransType));

    qbrPurchId                  = queryPwp.dataSourceTable(tableNum(VendInvoiceJour)).findRange(fieldNum(VendInvoiceJour, PurchId));
    qbrPwp                      = queryPwp.dataSourceTable(tableNum(VendInvoiceTrans)).findRange(fieldNum(VendInvoiceTrans, IsPwp));

    qbrProjPurch                = queryPwp.dataSourceTable(tableNum(ProjTable), 1).findRange(fieldNum(ProjTable, ProjId));
    qbrProjJournal              = queryPwp.dataSourceTable(tableNum(ProjTable), 2).findRange(fieldNum(ProjTable, ProjId));

    qbrContractPurch            = queryPwp.dataSourceTable(tableNum(ProjTable), 1).findRange(fieldNum(ProjTable, ProjInvoiceProjId));
    qbrContractJournal          = queryPwp.dataSourceTable(tableNum(ProjTable), 2).findRange(fieldNum(ProjTable, ProjInvoiceProjId));
    qbrPayWhenPaid              = queryPwp.dataSourceTable(tableNum(LedgerJournalTrans_Project), 1).findRange(fieldNum(LedgerJournalTrans_Project, IsPwp));

    qbrInvoicePurch.value(SysQuery::valueNotEmptyString());
    qbrInvoiceJournal.value(SysQuery::valueNotEmptyString());

    this.query(queryPwp);
}

public int active()
{
    int ret;

    ret = super();

    element.setButtonAccess();

    element.fetchCustomerInvoiceDetails();

    return ret;
}

display CustName custName(ProjInvoiceJour _projInvoiceJour)
{
    return CustTable::find(_projInvoiceJour.InvoiceAccount).name();
}

public void executeQuery()
{
    if (projInvoiceIds != '')
    {
        this.queryBuildDataSource().addRange(fieldNum(ProjInvoiceJour, ProjInvoiceId)).value(projInvoiceIds);
        super();
        this.queryBuildDataSource().clearRange(fieldNum(ProjInvoiceJour, ProjInvoiceId));
    }
}

public int active()
{
    int ret;

    ret = super();

    element.fetchCustomerInvoiceDetails();

    return ret;
}

public void executeQuery()
{
    if (VendTrans && VendTrans.TransType != LedgerTransType::Purch)
    {
        qbrJournalTransVoucher.value(SysQuery::value(VendTrans.Voucher));
        qbrJournalTransDate.value(SysQuery::value(VendTrans.TransDate));
    }
    else
    {
        qbrJournalTransVoucher.value(SysQuery::value(-1));
    }

    super();
}

public void init()
{
    super();

    // The default link between vendTrans and LedgerJournalTrans is done through
    // a relation that is not the one we want here, so we manually add a range
    // and update it with the proper values on executeQuery

    this.queryBuildDataSource().clearDynalinks();
    qbrJournalTransVoucher = SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(LedgerJournalTrans, Voucher));
    qbrJournalTransDate   = SysQuery::findOrCreateRange(this.queryBuildDataSource(), fieldNum(LedgerJournalTrans, TransDate));
}

public void updateDesign()
{
    if (LedgerJournalTrans_Project.ReadyForPayment)
    {
        ReleasePWPJournalLineButton.enabled(false);
        EnforcePWPJournalLineButton.enabled(true);
    }
    else
    {
        ReleasePWPJournalLineButton.enabled(true);
        EnforcePWPJournalLineButton.enabled(false);
    }
}

public int active()
{
    int ret;

    ret = super();

    this.updateDesign();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    fromDate = this.dateValue();

    if (toDate && fromDate > toDate)
    {
        throw error("@SYS30562");
    }

    qbrTransDatePurch.value(SysQuery::range(fromDate, toDate));
    qbrTransDateJournal.value(SysQuery::range(fromDate, toDate));
    vendTrans_ds.executeQuery();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    toDate = this.dateValue();

    if (fromDate > toDate)
    {
        throw error("@SYS30562");
    }

    qbrTransDatePurch.value(SysQuery::range(fromDate, toDate));
    qbrTransDateJournal.value(SysQuery::range(fromDate, toDate));

    vendTrans_ds.executeQuery();

    return ret;
}

