public void lookup()
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(VendTrans), this);

    sysTableLookup.addLookupfield(fieldNum(VendTrans, Invoice));
    sysTableLookup.addLookupfield(fieldNum(VendTrans, AccountNum));

    sysTableLookup.parmQuery(new Query(queryPwp));
    sysTableLookup.performFormLookup();
}

