public void lookup()
{
    SysTableLookup          sysTableLookup;
    Query                   query;
    QueryBuildDataSource    qbdsProjTable;

    sysTableLookup = SysTableLookup::newParameters(tableNum(ProjTable), this);

    query           = new Query();
    qbdsProjTable   = query.addDataSource(tableNum(ProjTable));

    if (projContract)
    {
        qbdsProjTable.addRange(fieldNum(ProjTable, ProjInvoiceProjId)).value(projContract);
    }

    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjId));

    if (!ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled())
    {
        sysTableLookup.addLookupfield(fieldNum(ProjTable, Type));
    }

    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjGroupId));
    sysTableLookup.addLookupfield(fieldNum(ProjTable, CustAccount));
    sysTableLookup.addLookupfield(fieldNum(ProjTable, ProjInvoiceProjId));
    sysTableLookup.addLookupfield(fieldNum(ProjTable, ParentId));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    projId = this.valueStr();

    if (projId)
    {
        projectContract.text(ProjTable::find(projId).ProjInvoiceProjId);
    }
    else
    {
        projectContract.text('');
    }

    qbrProjJournal.value(projId);
    qbrProjPurch.value(projId);

    vendTrans_ds.executeQuery();

    return ret;
}

