public boolean modified()
{
    boolean ret;

    ret = super();

    invoiceStatus = this.selection();

    if (invoiceStatus == PSAVendInvoiceStatus::All)
    {
        qbrVendInvoiceStatusPurch.value("");
        qbrVendInvoiceStatusJournal.value("");
    }
    else if (invoiceStatus == PSAVendInvoiceStatus::Open)
    {
        qbrVendInvoiceStatusPurch.value(SysQuery::valueEmptyString());
        qbrVendInvoiceStatusJournal.value(SysQuery::valueEmptyString());
    }
    else
    {
        qbrVendInvoiceStatusPurch.value(SysQuery::valueNotEmptyString());
        qbrVendInvoiceStatusJournal.value(SysQuery::valueNotEmptyString());
    }

    vendTrans_ds.executeQuery();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    isPwp = this.value();

    if (isPwp == NoYes::Yes)
    {
        qbrPwp.value(queryValue(isPwp));
        qbrPayWhenPaid.value(queryValue(isPwp));
    }
    else
    {
        qbrPwp.value(SysQuery::valueUnlimited());
        qbrPayWhenPaid.value(SysQuery::valueUnlimited());
    }

    vendTrans_ds.executeQuery();

    return ret;
}

public void lookup()
{
    SysTableLookup          sysTableLookup;
    Query                   query;
    QueryBuildDataSource    qbdsPurchTable;

    sysTableLookup = SysTableLookup::newParameters(tableNum(PurchTable), this);

    query           = new Query();
    qbdsPurchTable  = query.addDataSource(tableNum(PurchTable));

    if (vendorAccount)
    {
        qbdsPurchTable.addRange(fieldNum(PurchTable, OrderAccount)).value(vendorAccount);
    }
    else if (projId)
    {
        qbdsPurchTable.addRange(fieldNum(PurchTable, ProjId)).value(projId);
    }
    else if (vendorAccount && projId)
    {
        qbdsPurchTable.addRange(fieldNum(PurchTable, ProjId)).value(projId);
        qbdsPurchTable.addRange(fieldNum(PurchTable, OrderAccount)).value(vendorAccount);
    }

    sysTableLookup.addLookupfield(fieldNum(PurchTable, PurchId));
    sysTableLookup.addLookupfield(fieldNum(PurchTable, PurchName));
    sysTableLookup.addLookupfield(fieldNum(PurchTable, OrderAccount));
    sysTableLookup.addLookupfield(fieldNum(PurchTable, PurchStatus));
    sysTableLookup.addLookupfield(fieldNum(PurchTable, ProjId));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

