[Form]
public class PaymCalendarRules extends FormRun implements PaymCalendarICreateRule
{
    [FormObservable]
    public PaymCalendarModuleType paymCalendarModuleType;
    private List priorityLocations;
    private LegalEntity legalEntity;
    private Name companyCalendarName;

}

public void enableFields()
{
    specificGroup.visible(false);
    location.visible(false);
    companyLocation.visible(false);
    PaymCalendarCriteriaRule_PaymCalendar.mandatory(false);

    switch (calendarRule.RuleType)
    {
        case PaymCalendarRuleType::Specific :
            specificGroup.visible(true);
            PaymCalendarCriteriaRule_PaymCalendar.mandatory(true);
            break;
        case PaymCalendarRuleType::LegalEntityLocation :
            companyLocation.visible(true);
            companyCalendar.text(companyCalendarName);
            break;
        case PaymCalendarRuleType::ThirdPartyLocation :
            location.visible(true);
            break;
    }
}

public void enableSpecificFields()
{
    if (paymCalendarModuleType == PaymCalendarModuleType::Customer)
    {
        paymCalendarCriteriaRule_VendorPaymMode.visible(false);

        if (calendarRule_PaymCalendarCriteriaRule.CustomerPaymMode)
        {
            methodOfPaymentChoice.selection(PaymAllCode::Code);
            paymCalendarCriteriaRule_CustomerPaymMode.enabled(true);
        }
        else
        {
            methodOfPaymentChoice.selection(PaymAllCode::All);
            calendarRule_PaymCalendarCriteriaRule.CustomerPaymMode = '';
            paymCalendarCriteriaRule_CustomerPaymMode.enabled(false);
        }
    }
    else
    {
        paymCalendarCriteriaRule_CustomerPaymMode.visible(false);

        if (calendarRule_PaymCalendarCriteriaRule.VendorPaymMode)
        {
            methodOfPaymentChoice.selection(PaymAllCode::Code);
            paymCalendarCriteriaRule_VendorPaymMode.enabled(true);
        }
        else
        {
            methodOfPaymentChoice.selection(PaymAllCode::All);
            calendarRule_PaymCalendarCriteriaRule.VendorPaymMode = '';
            paymCalendarCriteriaRule_VendorPaymMode.enabled(false);
        }
    }

    if (calendarRule_PaymCalendarCriteriaRule.PaymTerm)
    {
        termsOfPaymentChoice.selection(PaymAllCode::Code);
        paymCalendarCriteriaRule_PaymTerm.enabled(true);
    }
    else
    {
        termsOfPaymentChoice.selection(PaymAllCode::All);
        calendarRule_PaymCalendarCriteriaRule.PaymTerm = '';
        paymCalendarCriteriaRule_PaymTerm.enabled(false);
    }

    calendarRule_PaymCalendarCriteriaRule_ds.refresh();
}

public void init()
{
    LogisticsPostalAddress postalAddress;
    LogisticsAddressCountryRegion countryRegion;
    PaymCalendarCountryRegion localPaymCalendar;

    super();

    if (this.args().menuItemName() == menuItemDisplayStr(PaymCalendarRuleVendor))
    {
        paymCalendarModuleType = PaymCalendarModuleType::Vendor;
        element.design().caption("@CashManagement:GLS220307");
        calendarRule_PaymCalendarCriteriaRule_ds.object(fieldNum(PaymCalendarCriteriaRule, CustomerPaymMode)).visible(false);
        defineHeaderSupplemental.text("@CashManagement:GLS220362");
        prioritizeText.text("@CashManagement:GLS220333");
        selectText.text("@CashManagement:GLS220341");
        location.caption("@CashManagement:GLS220325");
    }

    if (this.args().menuItemName() == menuItemDisplayStr(PaymCalendarRuleCustomer))
    {
        paymCalendarModuleType = PaymCalendarModuleType::Customer;
        element.design().caption("@CashManagement:GLS220348");
        calendarRule_PaymCalendarCriteriaRule_ds.object(fieldNum(PaymCalendarCriteriaRule, VendorPaymMode)).visible(false);
        defineHeaderSupplemental.text("@CashManagement:GLS220349");
        prioritizeText.text("@CashManagement:GLS220352");
        selectText.text("@CashManagement:GLS220353");
        location.caption("@CashManagement:GLS220422");
    }

    legalEntity = CompanyInfo::current();
    postalAddress = CompanyInfo::findDataArea(curext()).postalAddress();

    if (postalAddress)
    {
        countryRegion = LogisticsAddressCountryRegion::find(postalAddress.CountryRegionId);
        localPaymCalendar = PaymCalendarCountryRegion::findByCountryRegion(countryRegion.RecId);
        companyCalendarName = PaymCalendar::find(localPaymCalendar.PaymCalendar).Name;
    }

    this.enableFields();
}

