public void executeQuery()
{
    if (paymCalendarModuleType == PaymCalendarModuleType::Customer)
    {
        this.query().dataSourceName('CalendarRule').addRange(fieldNum(PaymCalendarRule, ModuleType)).value(int2str(enum2int(PaymCalendarModuleType::Customer)));
    }
    else
    {
        this.query().dataSourceName('CalendarRule').addRange(fieldNum(PaymCalendarRule, ModuleType)).value(int2str(enum2int(PaymCalendarModuleType::Vendor)));
