    super();
}

public edit NoYes editIncludeStateHoliday(boolean _set, PaymCalendarCountryRegion _countryRegion, NoYes _includeStateHoliday)
{
    NoYes hasIncludeStateHolidayBeenSet;
    PaymCalendarCountryRegionLegalEntity localPaymCalendarCountryLegalEntity;

    if (_set)
    {
        ttsbegin;

        localPaymCalendarCountryLegalEntity = PaymCalendarCountryRegionLegalEntity::findByLegalEntityTypePaymentCalendar(
            curExt(),
            paymCalendarModuleType,
            _countryRegion.RecId,
            true);

        if (localPaymCalendarCountryLegalEntity)
        {
            localPaymCalendarCountryLegalEntity.IncludeStateHoliday = !localPaymCalendarCountryLegalEntity.IncludeStateHoliday;
            localPaymCalendarCountryLegalEntity.update();
        }

        ttscommit;
    }
    else
    {
        localPaymCalendarCountryLegalEntity = PaymCalendarCountryRegionLegalEntity::findByLegalEntityTypePaymentCalendar(
            curExt(),
            paymCalendarModuleType,
            _countryRegion.RecId);
    }

    hasIncludeStateHolidayBeenSet = localPaymCalendarCountryLegalEntity.IncludeStateHoliday;

    return hasIncludeStateHolidayBeenSet;
}

public int active()
{
    int ret;
    PaymCalendarCountryRegionLegalEntity localPaymCalendarCountryLegalEntity;
    boolean stateHolidayExists;

    ret = super();

    stateHolidayExists = StateHoliday::existsForCountryRegion(paymCalendarCountryRegion.CountryRegion);

    if (stateHolidayExists)
    {
        localPaymCalendarCountryLegalEntity = PaymCalendarCountryRegionLegalEntity::findByLegalEntityTypePaymentCalendar(
            curExt(),
            paymCalendarModuleType,
            paymCalendarCountryRegion.RecId);

        stateHolidayExists = stateHolidayExists && localPaymCalendarCountryLegalEntity;
    }

    legalEntityCountryRegion_IncludeStateHoliday.allowEdit(stateHolidayExists);

    return ret;
}

public edit NoYes editCalendarCountryRegionLegalEntity(boolean _set, PaymCalendarCountryRegion _countryRegion, NoYes _includeStateHoliday)
{
    NoYes hasCountryRegionBeenSelected;
    PaymCalendarCountryRegionLegalEntity localPaymCalendarCountryLegalEntity;
    boolean stateHolidayExists;

    select firstonly RecId from localPaymCalendarCountryLegalEntity
        where localPaymCalendarCountryLegalEntity.PaymCalendarCountryRegion == _countryRegion.RecId
            && localPaymCalendarCountryLegalEntity.ModuleType == paymCalendarModuleType;

    if (localPaymCalendarCountryLegalEntity)
    {
        // If the main account is already included for budget control then mark the checkbox.
        hasCountryRegionBeenSelected = NoYes::Yes;
    }

    if (_set)
    {
        if (!localPaymCalendarCountryLegalEntity)
        {
            // Insert new country region legal entity record if the checkbox is marked.
            ttsbegin;

            localPaymCalendarCountryLegalEntity.PaymCalendarCountryRegion = _countryRegion.RecId;
            localPaymCalendarCountryLegalEntity.ModuleType = paymCalendarModuleType;
            stateHolidayExists = StateHoliday::existsForCountryRegion(_countryRegion.CountryRegion);
            localPaymCalendarCountryLegalEntity.IncludeStateHoliday = stateHolidayExists;

            localPaymCalendarCountryLegalEntity.insert();

            ttscommit;

            hasCountryRegionBeenSelected = NoYes::Yes;
        }
        else
        {
            // delete the country region legal entity record if the checkbox is marked.
            ttsbegin;

            delete_from localPaymCalendarCountryLegalEntity
                where localPaymCalendarCountryLegalEntity.PaymCalendarCountryRegion == _countryRegion.RecId
                    && localPaymCalendarCountryLegalEntity.ModuleType == paymCalendarModuleType;

            ttscommit;

            hasCountryRegionBeenSelected = NoYes::No;
        }
    }

    return hasCountryRegionBeenSelected;
}

public int active()
{
    int ret;
    PaymCalendarEvaluationPriority localPaymCalendarEvaluationPriority;

    ret = super();

    select firstonly Priority from localPaymCalendarEvaluationPriority
        order localPaymCalendarEvaluationPriority.Priority asc
        where localPaymCalendarEvaluationPriority.PaymCalendarRule == paymCalendarEvaluationPriority.PaymCalendarRule;

    if (localPaymCalendarEvaluationPriority.RecId == paymCalendarEvaluationPriority.RecId)
    {
        moveUp.enabled(false);
    }
    else
    {
        moveUp.enabled(true);
    }

    select firstonly Priority from localPaymCalendarEvaluationPriority
        order localPaymCalendarEvaluationPriority.Priority desc
        where localPaymCalendarEvaluationPriority.PaymCalendarRule == paymCalendarEvaluationPriority.PaymCalendarRule;

    if (localPaymCalendarEvaluationPriority.RecId == paymCalendarEvaluationPriority.RecId)
    {
        moveDown.enabled(false);
    }
    else
    {
        moveDown.enabled(true);
    }

    return ret;
}

/// <summary>
/// Returns the payment method <c>PaymMode</c> value for the specified <c>PaymCalendarRule</c> record.
/// </summary>
/// <param name="_calendarRule">
/// The <c>PaymCalendarRule</c> record.
/// </param>
/// <returns>
/// The payment method <c>PaymMode</c> value for the specified <c>PaymCalendarRule</c> record.
/// </returns>
public display String30 displayPaymentMethod(PaymCalendarRule _calendarRule)
{
    String30 paymTermSetting;
    PaymCalendarCriteriaRule localPaymCalendarCriteriaRule;
    boolean displayAll = true;

    if (_calendarRule.RuleType == PaymCalendarRuleType::Specific)
    {
        localPaymCalendarCriteriaRule = PaymCalendarCriteriaRule::find(_calendarRule.RecId);

        if (paymCalendarModuleType == PaymCalendarModuleType::Customer)
        {
            if (localPaymCalendarCriteriaRule.CustomerPaymMode)
            {
                paymTermSetting = strFmt("@GLS220471", CustPaymModeTable::find(localPaymCalendarCriteriaRule.CustomerPaymMode).PaymMode);
                displayAll = false;
            }
        }
        else
        {
            if (localPaymCalendarCriteriaRule.VendorPaymMode)
            {
                paymTermSetting = strFmt("@GLS220471", VendPaymModeTable::find(localPaymCalendarCriteriaRule.VendorPaymMode).PaymMode);
                displayAll = false;
            }
        }
    }

    if (displayAll)
    {
        paymTermSetting = "@GLS220472";
    }

    return paymTermSetting;
}

/// <summary>
/// Returns the payment term <c>PaymTermId</c> value for the specified <c>PaymCalendarRule</c> record.
/// </summary>
/// <param name="_calendarRule">
/// The <c>PaymCalendarRule</c> record.
/// </param>
/// <returns>
/// The payment term <c>PaymTermId</c> value for the specified <c>PaymCalendarRule</c> record.
/// </returns>
public display String30 displayPaymentTerm(PaymCalendarRule _calendarRule)
{
    String30 paymTermSetting;
    PaymCalendarCriteriaRule localPaymCalendarCriteriaRule;
    boolean displayAll = true;

    if (_calendarRule.RuleType == PaymCalendarRuleType::Specific)
    {
        localPaymCalendarCriteriaRule = PaymCalendarCriteriaRule::find(_calendarRule.RecId);
        if (localPaymCalendarCriteriaRule.PaymTerm)
        {
            paymTermSetting = strFmt("@GLS220471", PaymTerm::find(localPaymCalendarCriteriaRule.PaymTerm).PaymTermId);
            displayAll = false;
        }
    }

    if (displayAll)
    {
        paymTermSetting = "@GLS220472";
    }

    return paymTermSetting;
}

public int active()
{
    int ret;
    PaymCalendarRule localPaymCalendarRule;

    ret = super();

    select firstonly Priority from localPaymCalendarRule
        order localPaymCalendarRule.Priority asc
        where localPaymCalendarRule.ModuleType == paymCalendarModuleType
            && localPaymCalendarRule.IsActive == NoYes::Yes;

    if (localPaymCalendarRule.Priority >= PrioritizedRule.Priority)
    {
        prioritizeMoveUp.enabled(false);
    }
    else
    {
        prioritizeMoveUp.enabled(true);
    }

    select firstonly Priority from localPaymCalendarRule
        order localPaymCalendarRule.Priority desc
        where localPaymCalendarRule.ModuleType == paymCalendarModuleType
            && localPaymCalendarRule.IsActive == NoYes::Yes;

    if (localPaymCalendarRule.Priority <= PrioritizedRule.Priority)
    {
        priortizeMoveDown.enabled(false);
    }
    else
    {
        priortizeMoveDown.enabled(true);
    }

    return ret;
}

public void executeQuery()
{
    if (paymCalendarModuleType == PaymCalendarModuleType::Customer)
    {
        this.query().dataSourceName('PrioritizedRule').addRange(fieldNum(PaymCalendarRule, ModuleType)).value(int2str(enum2int(PaymCalendarModuleType::Customer)));
    }
    else
    {
        this.query().dataSourceName('PrioritizedRule').addRange(fieldNum(PaymCalendarRule, ModuleType)).value(int2str(enum2int(PaymCalendarModuleType::Vendor)));
    }

    this.query().dataSourceName('PrioritizedRule').addRange(fieldNum(PaymCalendarRule, IsActive)).value(int2Str(enum2int(NoYes::Yes)));

    super();
}

